import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export enum Screen {
  DASHBOARD = 'DASHBOARD',
  MAP = 'MAP',
  CALCULATOR = 'CALCULATOR',
  RESULTS = 'RESULTS',
  SETTINGS = 'SETTINGS',
  EMERGENCY = 'EMERGENCY'
}

export interface ImpactData {
  acUnits: number;
  fanUsage: number;
  energySource: string;
  co2Rate: number;
  gasLevels: number;
}

export interface FootprintResult {
  totalTons: number;
  breakdown: {
    acUsage: number;
    gasEmissions: number;
    energySource: number;
  };
}

export interface ReductionTip {
  id: string;
  title: string;
  description: string;
  impact: string;
  icon: string;
}

import React, { useState, useEffect } from 'react';
import { Screen, ImpactData, FootprintResult } from './types';
import Dashboard from './screens/Dashboard';
import Calculator from './screens/Calculator';
import Results from './screens/Results';
import Settings from './screens/Settings';
import EmergencyOverlay from './screens/EmergencyOverlay';
import BottomNav from './components/BottomNav';

const App: React.FC = () => {
  const [currentScreen, setCurrentScreen] = useState<Screen>(Screen.DASHBOARD);
  const [showEmergency, setShowEmergency] = useState(false);
  const [impactData, setImpactData] = useState<ImpactData>({
    acUnits: 2,
    fanUsage: 8,
    energySource: 'Main Grid (Coal/Gas)',
    co2Rate: 0,
    gasLevels: 0
  });

  const [results, setResults] = useState<FootprintResult | null>(null);

  const handleCalculate = (data: ImpactData) => {
    setImpactData(data);
    // Mock calculation logic
    const total = 2.5 + (data.acUnits * 0.3) + (data.fanUsage * 0.05);
    setResults({
      totalTons: parseFloat(total.toFixed(1)),
      breakdown: {
        acUsage: 40,
        gasEmissions: 35,
        energySource: 25
      }
    });
    setCurrentScreen(Screen.RESULTS);
  };

  const renderScreen = () => {
    switch (currentScreen) {
      case Screen.DASHBOARD:
        return <Dashboard 
          onShowEmergency={() => setShowEmergency(true)} 
          onViewImpact={() => setCurrentScreen(Screen.RESULTS)}
        />;
      case Screen.CALCULATOR:
        return <Calculator initialData={impactData} onCalculate={handleCalculate} />;
      case Screen.RESULTS:
        return <Results result={results} onBack={() => setCurrentScreen(Screen.DASHBOARD)} />;
      case Screen.SETTINGS:
        return <Settings />;
      default:
        return <Dashboard onShowEmergency={() => setShowEmergency(true)} onViewImpact={() => setCurrentScreen(Screen.RESULTS)} />;
    }
  };

  return (
    <div className="relative flex flex-col min-h-screen bg-pale-blue-50 max-w-md mx-auto shadow-2xl overflow-hidden">
      <main className="flex-1 overflow-y-auto hide-scrollbar pb-20">
        {renderScreen()}
      </main>

      {currentScreen !== Screen.RESULTS && (
        <BottomNav 
          currentScreen={currentScreen} 
          onNavigate={setCurrentScreen} 
        />
      )}

      {showEmergency && (
        <EmergencyOverlay onClose={() => setShowEmergency(false)} />
      )}
    </div>
  );
};

export default App;

import React from 'react';
