  const mapRef = useRef<any>(null);
  const markerRef = useRef<any>(null);
  const circleRef = useRef<any>(null);

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCoords, setSelectedCoords] = useState<{ lat: number, lng: number }>({
    lat: currentLocation.lat || 37.7749,
    lng: currentLocation.lng || -122.4194
  });
  const [previewLocation, setPreviewLocation] = useState<LocationData>(currentLocation);
  const [isSearching, setIsSearching] = useState(false);
  const [isGeocoding, setIsGeocoding] = useState(false);

  // Helper to get color based on AQI
  const getAqiTheme = (aqi: number) => {
    if (aqi <= 50) return { color: '#13ec5b', label: 'Healthy', bg: 'bg-aqi-good' };
    if (aqi <= 100) return { color: '#fde047', label: 'Moderate', bg: 'bg-aqi-moderate' };
    if (aqi <= 150) return { color: '#fb923c', label: 'Unhealthy', bg: 'bg-aqi-unhealthy' };
    return { color: '#ef4444', label: 'Hazardous', bg: 'bg-aqi-severe' };
  };

  useEffect(() => {
    if (!mapContainerRef.current) return;

    // Initialize map
    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false
    }).setView([selectedCoords.lat, selectedCoords.lng], 12);

    // Using CartoDB Voyager
    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    const theme = getAqiTheme(previewLocation.aqi);

    // Circle highlight
    circleRef.current = L.circle([selectedCoords.lat, selectedCoords.lng], {
      color: theme.color,
      fillColor: theme.color,
      fillOpacity: 0.2,
      radius: 1000,
      weight: 2
    }).addTo(mapRef.current);

    // Marker
    markerRef.current = L.marker([selectedCoords.lat, selectedCoords.lng], {
      draggable: true,
      icon: L.divIcon({
        className: 'custom-marker',
        html: `<div class="marker-container" style="--marker-color: ${theme.color}">
                <div class="marker-pulse"></div>
                <div class="marker-dot"></div>
               </div>`,
        iconSize: [40, 40],
        iconAnchor: [20, 20]
      })
    }).addTo(mapRef.current);

    // Handle map click
    mapRef.current.on('click', (e: any) => {
      const { lat, lng } = e.latlng;
      updateMarker(lat, lng);
    });

    // Handle marker drag
    markerRef.current.on('dragend', (e: any) => {
      const { lat, lng } = e.target.getLatLng();
      updateMarker(lat, lng);
    });

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
      }
    };
  }, []);

  const updateMarker = (lat: number, lng: number, shouldPan = false) => {
    setSelectedCoords({ lat, lng });
    if (markerRef.current) markerRef.current.setLatLng([lat, lng]);
    if (circleRef.current) circleRef.current.setLatLng([lat, lng]);
    if (shouldPan && mapRef.current) mapRef.current.panTo([lat, lng]);

    setIsSearching(true);

    // Simulate fetching localized AQI data
    setTimeout(() => {
      const newAqi = Math.floor(Math.random() * 250);
      const mockName = `Selected Point (${lat.toFixed(3)}, ${lng.toFixed(3)})`;
      const newLoc = {
        name: mockName,
