      // Update Visuals on Map
      const theme = getAqiTheme(newAqi);
      if (circleRef.current) circleRef.current.setStyle({ color: theme.color, fillColor: theme.color });
      if (markerRef.current) {
        markerRef.current.setIcon(L.divIcon({
          className: 'custom-marker',
          html: `<div class="marker-container" style="--marker-color: ${theme.color}">
                  <div class="marker-pulse"></div>
