      maxZoom: 19
    }).addTo(mapRef.current);

    const theme = getAqiTheme(previewLocation.aqi);

    // 1. Live User Location Marker
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        const { latitude, longitude } = pos.coords;
        userLocationMarkerRef.current = L.marker([latitude, longitude], {
          zIndexOffset: 1000,
          icon: L.divIcon({
            className: 'user-location-marker',
            html: `<div class="blue-dot-container">
                    <div class="blue-dot-pulse"></div>
                    <div class="blue-dot"></div>
                   </div>`,
            iconSize: [30, 30],
            iconAnchor: [15, 15]
          })
        }).addTo(mapRef.current);
      });
    }

    // 2. Selection Marker
    selectionMarkerRef.current = L.marker([selectedCoords.lat, selectedCoords.lng], {
      draggable: true,
      icon: L.divIcon({
        className: 'selection-marker',
        html: createMarkerHtml(theme.color),
        iconSize: [44, 44],
        iconAnchor: [22, 44]
      })
    }).addTo(mapRef.current);

    // 3. Radius Circle
    selectionCircleRef.current = L.circle([selectedCoords.lat, selectedCoords.lng], {
      color: theme.color,
      fillColor: theme.color,
      fillOpacity: 0.15,
      radius: 800,
      weight: 1
    }).addTo(mapRef.current);

    // Map Event Listeners
    mapRef.current.on('click', (e: any) => {
      updateLocation(e.latlng.lat, e.latlng.lng);
    });

    selectionMarkerRef.current.on('dragend', (e: any) => {
      const { lat, lng } = e.target.getLatLng();
      updateLocation(lat, lng);
    });

    return () => {
      if (mapRef.current) mapRef.current.remove();
    };
  }, []);

  const createMarkerHtml = (color: string) => `
    <div class="marker-pin-wrapper" style="--marker-color: ${color}">
      <div class="marker-pin-head">
        <div class="marker-pin-inner"></div>
      </div>
      <div class="marker-pin-tip"></div>
    </div>
  `;

  const updateLocation = (lat: number, lng: number, shouldPan = false) => {
    setSelectedCoords({ lat, lng });
    if (selectionMarkerRef.current) selectionMarkerRef.current.setLatLng([lat, lng]);
    if (selectionCircleRef.current) selectionCircleRef.current.setLatLng([lat, lng]);
    if (shouldPan && mapRef.current) mapRef.current.panTo([lat, lng]);

    setIsSearching(true);
    setGroundingLinks([]); // Clear old links

    // Simulate localized environmental data retrieval
    setTimeout(() => {
      const mockAqi = Math.floor(Math.random() * 250);
      const newLoc: LocationData = {
        name: `Location at ${lat.toFixed(3)}, ${lng.toFixed(3)}`,
        lat: lat,
        lng: lng,
        aqi: mockAqi,
        pm25: parseFloat((Math.random() * 40 + 2).toFixed(1)),
        no2: parseFloat((Math.random() * 20 + 1).toFixed(1))
      };

      setPreviewLocation(newLoc);
      setIsSearching(false);

      const theme = getAqiTheme(mockAqi);
      if (selectionCircleRef.current) selectionCircleRef.current.setStyle({ color: theme.color, fillColor: theme.color });
      if (selectionMarkerRef.current) {
        selectionMarkerRef.current.setIcon(L.divIcon({
          className: 'selection-marker',
          html: createMarkerHtml(theme.color),
          iconSize: [44, 44],
          iconAnchor: [22, 44]
        }));
      }
    }, 800);
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsGeocoding(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

      // Using Gemini 2.5 Flash for Google Maps Grounding
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Find the precise coordinates (latitude and longitude) for the location: "${searchQuery}". 
                   Return the coordinates in your text response like "COORDS: [LAT, LNG]".
                   Also provide helpful details about the place.`,
        config: {
          tools: [{ googleMaps: {} }],
          toolConfig: {
            retrievalConfig: {
              latLng: {
                latitude: selectedCoords.lat,
                longitude: selectedCoords.lng
              }
            }
          }
        },
      });

      const text = response.text || "";
      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];

      // Extract coordinates from AI text response
      const coordMatch = text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);

      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);

        mapRef.current.setView([lat, lng], 14);
        updateLocation(lat, lng);
        setPreviewLocation(prev => ({ ...prev, name: searchQuery }));
        setGroundingLinks(chunks);
      } else {
        // Fallback: If AI doesn't return text coords but has map tool info, try to use tool result
        const mapChunk = chunks.find(c => c.maps?.uri);
        if (mapChunk) {
            // We show the links but can't pan without coords. 
            // Most users will prefer Nominatim for coords if Gemini doesn't extract them.
            setGroundingLinks(chunks);
        }

        // Simple fallback geocode for map movement if AI text fails
        const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}`);
        const geoData = await geoResponse.json();
        if (geoData?.[0]) {
          const lat = parseFloat(geoData[0].lat);
          const lng = parseFloat(geoData[0].lon);
          mapRef.current.setView([lat, lng], 14);
          updateLocation(lat, lng);
          setPreviewLocation(prev => ({ ...prev, name: geoData[0].display_name }));
          setGroundingLinks(chunks);
        }
      }
    } catch (err) {
      console.error("Grounding error:", err);
      alert("Search failed. Check your network or query.");
    } finally {
      setIsGeocoding(false);
    }
  };

  const theme = getAqiTheme(previewLocation.aqi);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* Map Content */}
      <div ref={mapContainerRef} className="h-full w-full z-0" />

      {/* Top Floating UI: Search & Legend */}
      <div className="absolute top-6 left-1/2 -translate-x-1/2 w-[92%] z-[10] space-y-3">
        {/* Search Bar */}
        <form onSubmit={handleSearch} className="relative group">
          <div className="absolute inset-y-0 left-4 flex items-center pointer-events-none">
            <span className={`material-symbols-outlined text-primary/60 ${isGeocoding ? 'animate-spin' : ''}`}>
              {isGeocoding ? 'sync' : 'search'}
            </span>
          </div>
          <input 
            type="text"
            className="w-full pl-12 pr-12 py-4 bg-white/95 backdrop-blur-2xl border border-white rounded-[2rem] shadow-2xl text-sm font-semibold placeholder:text-slate-400 focus:outline-none focus:ring-4 focus:ring-primary/10 transition-all"
            placeholder="Search with Google Maps..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button 
            type="button"
            onClick={() => {
              if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(pos => {
                  const { latitude, longitude } = pos.coords;
                  mapRef.current.setView([latitude, longitude], 15);
                  updateLocation(latitude, longitude);
                });
              }
            }}
            className="absolute right-3 top-1/2 -translate-y-1/2 bg-primary/10 hover:bg-primary/20 p-2.5 rounded-full text-primary transition-colors"
          >
            <span className="material-symbols-outlined text-xl">my_location</span>
          </button>
        </form>

        {/* Status Legend */}
        <div className="flex justify-center gap-1.5 overflow-x-auto hide-scrollbar pb-1">
          {[
            { label: 'Healthy', color: '#10b981' },
            { label: 'Moderate', color: '#f59e0b' },
            { label: 'Unhealthy', color: '#f97316' },
            { label: 'Hazardous', color: '#e11d48' }
          ].map(item => (
            <div key={item.label} className="bg-white/90 backdrop-blur-md px-3 py-1.5 rounded-full border border-white shadow-sm flex items-center gap-2 shrink-0">
              <div className="w-2.5 h-2.5 rounded-full" style={{ backgroundColor: item.color }}></div>
              <span className="text-[10px] font-black text-slate-600 uppercase tracking-tighter">{item.label}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Floating Bottom Panel */}
      <div className="absolute bottom-24 left-1/2 -translate-x-1/2 w-[94%] z-[10]">
        <div className="bg-white/95 backdrop-blur-3xl rounded-[2.5rem] p-6 shadow-2xl border border-white/80 overflow-hidden relative">
          {/* Loading Overlay */}
          {isSearching && (
            <div className="absolute inset-0 bg-white/60 backdrop-blur-[2px] z-20 flex items-center justify-center">
              <div className="flex flex-col items-center">
                <div className="w-12 h-12 border-4 border-primary/20 border-t-primary rounded-full animate-spin"></div>
                <p className="mt-2 text-[10px] font-bold text-primary uppercase tracking-widest">Scanning Area...</p>
              </div>
            </div>
          )}

          <div className="flex justify-between items-start mb-4">
            <div className="flex-1 mr-4">
              <div className="flex items-center gap-2 mb-1.5">
                <span className={`px-2 py-0.5 rounded-md text-[9px] font-black uppercase text-white ${theme.bg}`}>
                  {theme.label} Zone
                </span>
                <span className="text-[10px] font-bold text-slate-400 uppercase tracking-wider">Verified Data</span>
              </div>
              <h3 className="text-slate-900 font-bold text-lg truncate leading-tight">
                {previewLocation.name}
              </h3>
              <p className="text-slate-400 text-[10px] font-medium mt-1">
                Pointed at {selectedCoords.lat.toFixed(4)}, {selectedCoords.lng.toFixed(4)}
              </p>
            </div>
            <div className={`flex flex-col items-center justify-center p-3 rounded-3xl shadow-lg border-2 border-white min-w-[70px] ${theme.bg}`}>
              <span className="text-[8px] font-black text-white/60 uppercase leading-none mb-1">AQI</span>
              <span className="text-2xl font-black text-white leading-none">
                {previewLocation.aqi}
              </span>
            </div>
          </div>

          {/* Grounding Links Section */}
          {groundingLinks.length > 0 && (
            <div className="mb-4 flex flex-wrap gap-2">
              {groundingLinks.map((chunk, i) => chunk.maps && (
                <a 
                  key={i} 
                  href={chunk.maps.uri} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="inline-flex items-center gap-1.5 bg-blue-50 border border-blue-100 px-2 py-1 rounded-lg text-[10px] font-bold text-blue-600 hover:bg-blue-100 transition-colors"
                >
                  <span className="material-symbols-outlined text-[14px]">map</span>
                  {chunk.maps.title || "View on Google Maps"}
                </a>
              ))}
            </div>
          )}

          <div className="grid grid-cols-2 gap-4 mb-6">
            <div className="bg-slate-50/80 p-4 rounded-3xl flex items-center gap-4 border border-slate-100">
              <div className="size-10 rounded-2xl bg-primary/10 flex items-center justify-center text-primary">
                <span className="material-symbols-outlined text-xl">filter_alt</span>
              </div>
              <div className="flex flex-col">
                <span className="text-[9px] font-bold text-slate-400 uppercase">PM 2.5</span>
                <span className="text-base font-bold text-slate-800">{previewLocation.pm25}</span>
              </div>
            </div>
            <div className="bg-slate-50/80 p-4 rounded-3xl flex items-center gap-4 border border-slate-100">
              <div className="size-10 rounded-2xl bg-primary/10 flex items-center justify-center text-primary">
                <span className="material-symbols-outlined text-xl">co2</span>
              </div>
              <div className="flex flex-col">
                <span className="text-[9px] font-bold text-slate-400 uppercase">NO2</span>
                <span className="text-base font-bold text-slate-800">{previewLocation.no2}</span>
              </div>
            </div>
          </div>

          <button 
            onClick={() => onLocationSelect(previewLocation)}
            className="w-full py-5 rounded-[1.5rem] bg-slate-900 text-white font-black text-base shadow-xl shadow-slate-900/20 active:scale-95 transition-all flex items-center justify-center gap-3"
          >
            <span>Lock In Location</span>
            <span className="material-symbols-outlined text-xl">check_circle</span>
          </button>
        </div>
      </div>

      <style>{`
        .user-location-marker { background: transparent; border: none; }
        .blue-dot-container {
          position: relative;
          width: 30px;
          height: 30px;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .blue-dot {
          width: 14px;
          height: 14px;
          background-color: #3b82f6;
          border: 3px solid white;
          border-radius: 50%;
          box-shadow: 0 0 10px rgba(0,0,0,0.3);
          z-index: 2;
        }
        .blue-dot-pulse {
          position: absolute;
          width: 100%;
          height: 100%;
          background-color: #3b82f6;
          border-radius: 50%;
          opacity: 0.4;
          animation: pulse-ring 2s infinite;
        }
        @keyframes pulse-ring {
          0% { transform: scale(0.5); opacity: 0.8; }
          100% { transform: scale(1.5); opacity: 0; }
        }

        .selection-marker { background: transparent; border: none; }
        .marker-pin-wrapper {
          position: relative;
          width: 44px;
          height: 44px;
          display: flex;
          flex-direction: column;
          align-items: center;
          cursor: grab;
          transition: transform 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
        }
        .marker-pin-wrapper:active { transform: scale(1.1) translateY(-10px); cursor: grabbing; }
        .marker-pin-head {
          width: 36px;
          height: 36px;
          background-color: var(--marker-color);
          border-radius: 50% 50% 50% 0;
          transform: rotate(-45deg);
          border: 3px solid white;
          box-shadow: 0 8px 16px rgba(0,0,0,0.25);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2;
        }
        .marker-pin-inner {
          width: 12px;
          height: 12px;
          background: white;
          border-radius: 50%;
        }
        .marker-pin-tip {
          width: 4px;
          height: 4px;
          background: var(--marker-color);
          border-radius: 50%;
          margin-top: -2px;
          box-shadow: 0 10px 10px rgba(0,0,0,0.3);
        }
      `}</style>
    </div>
  );
};

export default MapScreen;

import React, { useEffect, useRef, useState } from 'react';
import { LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

// Declare L for Leaflet global from script tag
declare const L: any;

interface MapScreenProps {
  currentLocation: LocationData;
  onLocationSelect: (location: LocationData) => void;
}

interface SearchResult {
  title: string;
  uri: string;
  snippet?: string;
  lat?: number;
  lng?: number;
}

const MapScreen: React.FC<MapScreenProps> = ({ currentLocation, onLocationSelect }) => {
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapRef = useRef<any>(null);
  const selectionMarkerRef = useRef<any>(null);
  const userLocationMarkerRef = useRef<any>(null);
  const selectionCircleRef = useRef<any>(null);

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCoords, setSelectedCoords] = useState<{ lat: number, lng: number }>({
    lat: currentLocation.lat || 37.7749,
    lng: currentLocation.lng || -122.4194
  });
  const [previewLocation, setPreviewLocation] = useState<LocationData>(currentLocation);
  const [isSearching, setIsSearching] = useState(false);
  const [isGeocoding, setIsGeocoding] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  // Helper to get theme based on AQI
  const getAqiTheme = (aqi: number) => {
    if (aqi <= 50) return { color: '#10b981', label: 'Healthy', bg: 'bg-emerald-500', text: 'text-emerald-500' };
    if (aqi <= 100) return { color: '#f59e0b', label: 'Moderate', bg: 'bg-amber-500', text: 'text-amber-500' };
    if (aqi <= 200) return { color: '#f97316', label: 'Unhealthy', bg: 'bg-orange-500', text: 'text-orange-500' };
    return { color: '#e11d48', label: 'Hazardous', bg: 'bg-rose-600', text: 'text-rose-600' };
  };

  useEffect(() => {
    if (!mapContainerRef.current) return;

    // Initialize map
    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false
    }).setView([selectedCoords.lat, selectedCoords.lng], 13);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    const theme = getAqiTheme(previewLocation.aqi);

    // Live User Location Marker
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        const { latitude, longitude } = pos.coords;
        userLocationMarkerRef.current = L.marker([latitude, longitude], {
          zIndexOffset: 1000,
          icon: L.divIcon({
            className: 'user-location-marker',
            html: `<div class="blue-dot-container"><div class="blue-dot-pulse"></div><div class="blue-dot"></div></div>`,
            iconSize: [30, 30],
            iconAnchor: [15, 15]
          })
        }).addTo(mapRef.current);
      });
    }

    // Selection Marker
    selectionMarkerRef.current = L.marker([selectedCoords.lat, selectedCoords.lng], {
      draggable: true,
      icon: L.divIcon({
        className: 'selection-marker',
        html: createMarkerHtml(theme.color),
        iconSize: [44, 44],
        iconAnchor: [22, 44]
      })
    }).addTo(mapRef.current);

    // Radius Circle
    selectionCircleRef.current = L.circle([selectedCoords.lat, selectedCoords.lng], {
      color: theme.color,
      fillColor: theme.color,
      fillOpacity: 0.15,
      radius: 800,
      weight: 1
    }).addTo(mapRef.current);

    // Map Event Listeners
    mapRef.current.on('click', (e: any) => {
      updateLocation(e.latlng.lat, e.latlng.lng);
      setShowResults(false);
    });

    selectionMarkerRef.current.on('dragend', (e: any) => {
      const { lat, lng } = e.target.getLatLng();
      updateLocation(lat, lng);
    });

    return () => {
      if (mapRef.current) mapRef.current.remove();
    };
  }, []);

  const createMarkerHtml = (color: string) => `
    <div class="marker-pin-wrapper" style="--marker-color: ${color}">
      <div class="marker-pin-head"><div class="marker-pin-inner"></div></div>
      <div class="marker-pin-tip"></div>
    </div>
  `;

  const updateLocation = (lat: number, lng: number, name?: string, shouldPan = false) => {
    setSelectedCoords({ lat, lng });
    if (selectionMarkerRef.current) selectionMarkerRef.current.setLatLng([lat, lng]);
    if (selectionCircleRef.current) selectionCircleRef.current.setLatLng([lat, lng]);
    if (shouldPan && mapRef.current) mapRef.current.flyTo([lat, lng], 14);

    setIsSearching(true);

    setTimeout(() => {
      const mockAqi = Math.floor(Math.random() * 250);
      const newLoc: LocationData = {
        name: name || `Point at ${lat.toFixed(3)}, ${lng.toFixed(3)}`,
        lat: lat,
        lng: lng,
        aqi: mockAqi,
        pm25: parseFloat((Math.random() * 40 + 2).toFixed(1)),
        no2: parseFloat((Math.random() * 20 + 1).toFixed(1))
      };

      setPreviewLocation(newLoc);
      setIsSearching(false);

      const theme = getAqiTheme(mockAqi);
      if (selectionCircleRef.current) selectionCircleRef.current.setStyle({ color: theme.color, fillColor: theme.color });
      if (selectionMarkerRef.current) {
        selectionMarkerRef.current.setIcon(L.divIcon({
          className: 'selection-marker',
          html: createMarkerHtml(theme.color),
          iconSize: [44, 44],
          iconAnchor: [22, 44]
        }));
      }
    }, 800);
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsGeocoding(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Find the precise coordinates and location details for: "${searchQuery}". 
                   If there are multiple matches, list them. 
                   Format answer as: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: {
          tools: [{ googleMaps: {} }],
        },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Found Location",
          uri: c.maps.uri,
          snippet: ""
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      // If text contains specific coords, use them for the primary result
      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        updateLocation(lat, lng, searchQuery, true);
        if (extractedResults.length === 0) setShowResults(false);
      } else {
        // Fallback to OSM Nominatim if Gemini tool doesn't explicitly return coordinates in text
        const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}`);
        const geoData = await geoResponse.json();
        if (geoData?.[0]) {
          const lat = parseFloat(geoData[0].lat);
          const lng = parseFloat(geoData[0].lon);
          updateLocation(lat, lng, geoData[0].display_name, true);
        }
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsGeocoding(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsGeocoding(true);

    // Resolve coordinates for the specific clicked result using Nominatim (fastest for specific titles)
    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        updateLocation(parseFloat(geoData[0].lat), parseFloat(geoData[0].lon), result.title, true);
      }
    } catch (e) {
      console.error(e);
    } finally {
      setIsGeocoding(false);
    }
  };

  const theme = getAqiTheme(previewLocation.aqi);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      <div ref={mapContainerRef} className="h-full w-full z-0" />

      {/* Google-Style Search Interface */}
      <div className="absolute top-6 left-1/2 -translate-x-1/2 w-[92%] z-[20] flex flex-col items-center">
        <form onSubmit={handleSearch} className="w-full relative shadow-2xl rounded-full overflow-visible">
          <div className="flex items-center bg-white/95 backdrop-blur-2xl rounded-full border border-white p-1.5 pr-3">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isGeocoding ? 'animate-spin' : ''}`}>
                {isGeocoding ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              type="text"
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-semibold text-slate-800 placeholder:text-slate-400 py-3"
              placeholder="Search city, landmark, or area..."
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
