        .blue-dot {
          width: 14px; height: 14px; background-color: #3b82f6; border: 3px solid white; border-radius: 50%; box-shadow: 0 0 10px rgba(0,0,0,0.3); z-index: 2;
        }
        .blue-dot-pulse {
          position: absolute; width: 100%; height: 100%; background-color: #3b82f6; border-radius: 50%; opacity: 0.4; animation: pulse-ring 2s infinite;
        }
        @keyframes pulse-ring { 0% { transform: scale(0.5); opacity: 0.8; } 100% { transform: scale(1.5); opacity: 0; } }
        .selection-marker { background: transparent; border: none; }
        .marker-pin-wrapper {
          position: relative; width: 44px; height: 44px; display: flex; flex-direction: column; align-items: center; cursor: grab; transition: transform 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
        }
        .marker-pin-wrapper:active { transform: scale(1.1) translateY(-10px); cursor: grabbing; }
        .marker-pin-head {
          width: 36px; height: 36px; background-color: var(--marker-color); border-radius: 50% 50% 50% 0; transform: rotate(-45deg); border: 3px solid white; box-shadow: 0 8px 16px rgba(0,0,0,0.25); display: flex; align-items: center; justify-content: center; z-index: 2;
        }
        .marker-pin-inner { width: 12px; height: 12px; background: white; border-radius: 50%; }
        .marker-pin-tip { width: 4px; height: 4px; background: var(--marker-color); border-radius: 50%; margin-top: -2px; box-shadow: 0 10px 10px rgba(0,0,0,0.3); }
      `}</style>
