export default MapScreen;

import React, { useEffect, useRef, useState } from 'react';
import { LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

// Declare L for Leaflet global from script tag
declare const L: any;

interface MapScreenProps {
  currentLocation: LocationData;
  onLocationSelect: (location: LocationData) => void;
}

interface SearchResult {
  title: string;
  uri: string;
  snippet?: string;
  lat?: number;
  lng?: number;
}

const MapScreen: React.FC<MapScreenProps> = ({ currentLocation, onLocationSelect }) => {
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapRef = useRef<any>(null);
  const selectionMarkerRef = useRef<any>(null);
  const userLocationMarkerRef = useRef<any>(null);
  const selectionCircleRef = useRef<any>(null);

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCoords, setSelectedCoords] = useState<{ lat: number, lng: number }>({
    lat: currentLocation.lat || 37.7749,
    lng: currentLocation.lng || -122.4194
  });
  const [previewLocation, setPreviewLocation] = useState<LocationData>(currentLocation);
  const [isSearching, setIsSearching] = useState(false);
  const [isGeocoding, setIsGeocoding] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  // Helper to get theme based on AQI
  const getAqiTheme = (aqi: number) => {
    if (aqi <= 50) return { color: '#10b981', label: 'Healthy', bg: 'bg-emerald-500', text: 'text-emerald-500' };
    if (aqi <= 100) return { color: '#f59e0b', label: 'Moderate', bg: 'bg-amber-500', text: 'text-amber-500' };
    if (aqi <= 200) return { color: '#f97316', label: 'Unhealthy', bg: 'bg-orange-500', text: 'text-orange-500' };
    return { color: '#e11d48', label: 'Hazardous', bg: 'bg-rose-600', text: 'text-rose-600' };
  };

  useEffect(() => {
    if (!mapContainerRef.current) return;

    // Initialize map
    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false
    }).setView([selectedCoords.lat, selectedCoords.lng], 13);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    const theme = getAqiTheme(previewLocation.aqi);

    // Live User Location Marker
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        const { latitude, longitude } = pos.coords;
        userLocationMarkerRef.current = L.marker([latitude, longitude], {
          zIndexOffset: 1000,
          icon: L.divIcon({
            className: 'user-location-marker',
            html: `<div class="blue-dot-container"><div class="blue-dot-pulse"></div><div class="blue-dot"></div></div>`,
            iconSize: [30, 30],
            iconAnchor: [15, 15]
          })
        }).addTo(mapRef.current);
      });
    }

    // Selection Marker with enhanced info display
    selectionMarkerRef.current = L.marker([selectedCoords.lat, selectedCoords.lng], {
      draggable: true,
      icon: L.divIcon({
        className: 'selection-marker',
        html: createMarkerHtml(theme.color, previewLocation.aqi, theme.label),
        iconSize: [100, 60],
        iconAnchor: [50, 60]
      })
    }).addTo(mapRef.current);

    // Radius Circle
    selectionCircleRef.current = L.circle([selectedCoords.lat, selectedCoords.lng], {
      color: theme.color,
      fillColor: theme.color,
      fillOpacity: 0.15,
      radius: 800,
      weight: 1
    }).addTo(mapRef.current);

    // Map Event Listeners
    mapRef.current.on('click', (e: any) => {
      updateLocation(e.latlng.lat, e.latlng.lng);
      setShowResults(false);
    });

    selectionMarkerRef.current.on('dragend', (e: any) => {
      const { lat, lng } = e.target.getLatLng();
      updateLocation(lat, lng);
    });

    return () => {
      if (mapRef.current) mapRef.current.remove();
    };
  }, []);

  const createMarkerHtml = (color: string, aqi: number, label: string) => `
    <div class="marker-info-bubble" style="--marker-color: ${color}">
      <div class="bubble-content">
        <span class="bubble-label">${label}</span>
        <span class="bubble-aqi">${aqi}</span>
      </div>
      <div class="bubble-tail"></div>
    </div>
  `;

  const updateLocation = (lat: number, lng: number, name?: string, shouldPan = false) => {
    setSelectedCoords({ lat, lng });
    if (selectionMarkerRef.current) selectionMarkerRef.current.setLatLng([lat, lng]);
    if (selectionCircleRef.current) selectionCircleRef.current.setLatLng([lat, lng]);
    if (shouldPan && mapRef.current) mapRef.current.flyTo([lat, lng], 14);

    setIsSearching(true);

    setTimeout(() => {
      const mockAqi = Math.floor(Math.random() * 250);
      const theme = getAqiTheme(mockAqi);
      const newLoc: LocationData = {
        name: name || `Point at ${lat.toFixed(3)}, ${lng.toFixed(3)}`,
        lat: lat,
        lng: lng,
        aqi: mockAqi,
        pm25: parseFloat((Math.random() * 40 + 2).toFixed(1)),
        no2: parseFloat((Math.random() * 20 + 1).toFixed(1))
      };

      setPreviewLocation(newLoc);
      setIsSearching(false);

      if (selectionCircleRef.current) selectionCircleRef.current.setStyle({ color: theme.color, fillColor: theme.color });
      if (selectionMarkerRef.current) {
        selectionMarkerRef.current.setIcon(L.divIcon({
          className: 'selection-marker',
          html: createMarkerHtml(theme.color, mockAqi, theme.label),
          iconSize: [100, 60],
          iconAnchor: [50, 60]
        }));
      }
    }, 800);
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsGeocoding(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Find the precise coordinates and location details for: "${searchQuery}". 
                   If there are multiple matches, list them. 
                   Format answer as: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: {
          tools: [{ googleMaps: {} }],
        },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Found Location",
          uri: c.maps.uri,
          snippet: ""
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        updateLocation(lat, lng, searchQuery, true);
        if (extractedResults.length === 0) setShowResults(false);
      } else {
        const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}`);
        const geoData = await geoResponse.json();
        if (geoData?.[0]) {
          const lat = parseFloat(geoData[0].lat);
          const lng = parseFloat(geoData[0].lon);
          updateLocation(lat, lng, geoData[0].display_name, true);
        }
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsGeocoding(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsGeocoding(true);

    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        updateLocation(parseFloat(geoData[0].lat), parseFloat(geoData[0].lon), result.title, true);
      }
    } catch (e) {
      console.error(e);
    } finally {
      setIsGeocoding(false);
    }
  };

  const theme = getAqiTheme(previewLocation.aqi);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      <div ref={mapContainerRef} className="h-full w-full z-0" />

      {/* Google-Style Search Interface */}
      <div className="absolute top-6 left-1/2 -translate-x-1/2 w-[92%] z-[20] flex flex-col items-center">
        <form onSubmit={handleSearch} className="w-full relative shadow-2xl rounded-full overflow-visible">
          <div className="flex items-center bg-white/95 backdrop-blur-2xl rounded-full border border-white p-1.5 pr-3">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isGeocoding ? 'animate-spin' : ''}`}>
                {isGeocoding ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              type="text"
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-semibold text-slate-800 placeholder:text-slate-400 py-3"
              placeholder="Search city, landmark, or area..."
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
              <button type="button" onClick={() => { setSearchQuery(''); setSearchResults([]); setShowResults(false); }} className="p-2 text-slate-400 hover:text-slate-600 transition-colors">
                <span className="material-symbols-outlined text-lg">close</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-200 mx-1"></div>
            <button 
              type="button"
              onClick={() => {
                if (navigator.geolocation) {
                  navigator.geolocation.getCurrentPosition(pos => {
                    const { latitude, longitude } = pos.coords;
                    updateLocation(latitude, longitude, "Current Location", true);
                  });
                }
              }}
              className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all"
            >
              <span className="material-symbols-outlined text-[22px]">my_location</span>
            </button>
          </div>

          {/* Autocomplete Results */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-white/80 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2 duration-300">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Suggestions</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Powered by Google</span>
              </div>
              {searchResults.map((result, i) => (
                <button 
                  key={i}
                  type="button"
                  onClick={() => selectResult(result)}
                  className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 transition-colors text-left group"
                >
                  <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors shrink-0">
                    <span className="material-symbols-outlined text-[20px]">location_on</span>
                  </div>
                  <div className="flex flex-col min-w-0">
                    <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                    <span className="text-[10px] text-slate-400 truncate mt-0.5">Explore this area on Maps</span>
                  </div>
                </button>
              ))}
            </div>
          )}
        </form>
      </div>

      {/* Floating Bottom Panel */}
      <div className={`absolute bottom-24 left-1/2 -translate-x-1/2 w-[94%] z-[10] transition-transform duration-500 ${showResults ? 'translate-y-[150%]' : 'translate-y-0'}`}>
        <div className="bg-white/95 backdrop-blur-3xl rounded-[2.5rem] p-6 shadow-2xl border border-white/80 overflow-hidden relative">
          {isSearching && (
            <div className="absolute inset-0 bg-white/60 backdrop-blur-[2px] z-20 flex items-center justify-center">
              <div className="flex flex-col items-center">
                <div className="w-10 h-10 border-[3px] border-primary/20 border-t-primary rounded-full animate-spin"></div>
              </div>
            </div>
          )}

          <div className="flex justify-between items-start mb-6">
            <div className="flex-1 mr-4">
              <div className="flex items-center gap-2 mb-1.5">
                <span className={`px-2 py-0.5 rounded-md text-[9px] font-black uppercase text-white ${theme.bg}`}>
                  {theme.label} Zone
                </span>
                <span className="text-[10px] font-bold text-slate-400 uppercase tracking-wider">Live Analysis</span>
              </div>
              <h3 className="text-slate-900 font-bold text-lg leading-tight line-clamp-2">
                {previewLocation.name}
              </h3>
            </div>
            <div className={`flex flex-col items-center justify-center p-3 rounded-3xl shadow-lg border-2 border-white min-w-[70px] ${theme.bg}`}>
              <span className="text-[8px] font-black text-white/60 uppercase leading-none mb-1">AQI</span>
              <span className="text-2xl font-black text-white leading-none">{previewLocation.aqi}</span>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4 mb-6">
            <div className="bg-slate-50/80 p-4 rounded-3xl flex items-center gap-4 border border-slate-100">
              <div className="size-10 rounded-2xl bg-primary/10 flex items-center justify-center text-primary"><span className="material-symbols-outlined text-xl">filter_alt</span></div>
              <div className="flex flex-col"><span className="text-[9px] font-bold text-slate-400 uppercase">PM 2.5</span><span className="text-base font-bold text-slate-800">{previewLocation.pm25}</span></div>
            </div>
            <div className="bg-slate-50/80 p-4 rounded-3xl flex items-center gap-4 border border-slate-100">
              <div className="size-10 rounded-2xl bg-primary/10 flex items-center justify-center text-primary"><span className="material-symbols-outlined text-xl">co2</span></div>
              <div className="flex flex-col"><span className="text-[9px] font-bold text-slate-400 uppercase">NO2</span><span className="text-base font-bold text-slate-800">{previewLocation.no2}</span></div>
            </div>
          </div>

          <button 
            onClick={() => onLocationSelect(previewLocation)}
            className="w-full py-5 rounded-[1.5rem] bg-slate-900 text-white font-black text-base shadow-xl shadow-slate-900/20 active:scale-95 transition-all flex items-center justify-center gap-3"
          >
            <span>Lock In Selection</span>
            <span className="material-symbols-outlined text-xl">check_circle</span>
          </button>
        </div>
      </div>

      <style>{`
        .user-location-marker { background: transparent; border: none; }
        .blue-dot-container {
          position: relative; width: 30px; height: 30px; display: flex; align-items: center; justify-content: center;
        }
        .blue-dot {
          width: 14px; height: 14px; background-color: #3b82f6; border: 3px solid white; border-radius: 50%; box-shadow: 0 0 10px rgba(0,0,0,0.3); z-index: 2;
        }
        .blue-dot-pulse {
          position: absolute; width: 100%; height: 100%; background-color: #3b82f6; border-radius: 50%; opacity: 0.4; animation: pulse-ring 2s infinite;
        }
        @keyframes pulse-ring { 0% { transform: scale(0.5); opacity: 0.8; } 100% { transform: scale(1.5); opacity: 0; } }

        .selection-marker { background: transparent; border: none; overflow: visible !important; }
        .marker-info-bubble {
          position: relative;
          display: flex;
          flex-direction: column;
          align-items: center;
          filter: drop-shadow(0 4px 12px rgba(0,0,0,0.25));
          animation: drop-marker 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
        }
        @keyframes drop-marker {
          from { transform: translateY(-40px) scale(0.5); opacity: 0; }
          to { transform: translateY(0) scale(1); opacity: 1; }
        }
        .bubble-content {
          background-color: var(--marker-color);
          border: 2px solid white;
          border-radius: 12px;
          padding: 6px 12px;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          min-width: 80px;
          color: white;
        }
        .bubble-label {
          font-size: 8px;
          font-weight: 900;
          text-transform: uppercase;
          letter-spacing: 0.5px;
          opacity: 0.9;
          line-height: 1;
          margin-bottom: 2px;
        }
        .bubble-aqi {
          font-size: 20px;
          font-weight: 900;
          line-height: 1;
        }
        .bubble-tail {
          width: 0;
          height: 0;
          border-left: 8px solid transparent;
          border-right: 8px solid transparent;
          border-top: 10px solid var(--marker-color);
          margin-top: -1px;
        }
      `}</style>
    </div>
  );
};

