import React, { useState } from 'react';
import { LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

interface DashboardProps {
  location: LocationData;
  onLocationChange: (loc: LocationData) => void;
  onShowEmergency: () => void;
  onViewImpact: () => void;
}

interface SearchResult {
  title: string;
  uri: string;
}

const Dashboard: React.FC<DashboardProps> = ({ location, onLocationChange, onShowEmergency, onViewImpact }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  const handleGetLocation = () => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        // Simulating data for current location
        setTimeout(() => {
          onLocationChange({
            name: `My Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
            lat: latitude,
            lng: longitude,
            aqi: Math.floor(Math.random() * 60) + 10,
            pm25: parseFloat((Math.random() * 15 + 5).toFixed(1)),
            no2: parseFloat((Math.random() * 10 + 2).toFixed(1))
          });
          setIsLocating(false);
          setSearchQuery('');
          setShowResults(false);
        }, 1500);
      },
      (error) => {
        setIsLocating(false);
        alert('Unable to retrieve your location. ' + error.message);
      }
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Find precise coordinates and location details for: "${searchQuery}". 
                   If there are multiple matches, list them. 
                   Format answer as: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: {
          tools: [{ googleMaps: {} }],
        },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Found Location",
          uri: c.maps.uri
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        onLocationChange({
          name: searchQuery,
          lat,
          lng,
          aqi: Math.floor(Math.random() * 150) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
        if (extractedResults.length === 0) setShowResults(false);
      } else {
        // Fallback geocode
        const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}`);
        const geoData = await geoResponse.json();
        if (geoData?.[0]) {
          onLocationChange({
            name: geoData[0].display_name,
            lat: parseFloat(geoData[0].lat),
            lng: parseFloat(geoData[0].lon),
            aqi: Math.floor(Math.random() * 150) + 20,
            pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
            no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
          });
        }
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsLocating(true);

    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        onLocationChange({
          name: result.title,
          lat: parseFloat(geoData[0].lat),
          lng: parseFloat(geoData[0].lon),
          aqi: Math.floor(Math.random() * 150) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
      }
    } catch (e) {
      console.error(e);
    } finally {
      setIsLocating(false);
    }
  };

  const aqiColorClass = location.aqi <= 50 ? 'text-aqi-good' : 
                        location.aqi <= 100 ? 'text-aqi-moderate' : 
                        location.aqi <= 150 ? 'text-aqi-unhealthy' : 'text-aqi-severe';

  const aqiBgClass = location.aqi <= 50 ? 'bg-aqi-good/20' : 
                     location.aqi <= 100 ? 'bg-aqi-moderate/20' : 
                     location.aqi <= 150 ? 'bg-aqi-unhealthy/20' : 'bg-aqi-severe/20';

  const aqiBorderClass = location.aqi <= 50 ? 'border-aqi-good/30' : 
                         location.aqi <= 100 ? 'border-aqi-moderate/30' : 
                         location.aqi <= 150 ? 'border-aqi-unhealthy/30' : 'border-aqi-severe/30';

  const aqiLabel = location.aqi <= 50 ? 'Healthy' : 
                   location.aqi <= 100 ? 'Moderate' : 
                   location.aqi <= 150 ? 'Unhealthy' : 'Hazardous';

  const aqiStrokeColor = location.aqi <= 50 ? '#13ec5b' : 
                         location.aqi <= 100 ? '#fde047' : 
                         location.aqi <= 150 ? '#fb923c' : '#ef4444';

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-[#e0effe] to-[#f0f7ff] p-4">
      {/* Header with Google-Style Search */}
      <header className="flex flex-col pt-6 pb-2 gap-4 relative z-[100]">
        <div className="flex items-center justify-between px-1">
          <button className="p-2 -ml-2 text-pale-blue-900/70">
            <span className="material-symbols-outlined">menu</span>
          </button>
          <div className="flex flex-col items-center">
            <span className="text-[10px] uppercase tracking-widest text-primary font-bold">AirGuard</span>
            <div className="flex items-center gap-1">
              <span className={`material-symbols-outlined text-sm text-primary`}>location_on</span>
              <h2 className="text-pale-blue-900 text-sm font-semibold truncate max-w-[180px]">
                {location.name}
              </h2>
            </div>
          </div>
          <button className="p-2 -mr-2 text-primary">
            <span className="material-symbols-outlined">account_circle</span>
          </button>
        </div>

        <div className="relative">
          <form onSubmit={handleSearch} className="flex items-center bg-white/90 backdrop-blur-2xl rounded-full border border-white shadow-xl p-1.5 pr-3 transition-all duration-300 focus-within:ring-4 focus-within:ring-primary/10">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                {isLocating ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-semibold text-slate-800 placeholder:text-slate-400 py-2.5" 
              placeholder="Search city, landmark, or area..." 
              type="text" 
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
              <button 
                type="button" 
                onClick={() => { setSearchQuery(''); setSearchResults([]); setShowResults(false); }}
                className="p-2 text-slate-400 hover:text-slate-600 transition-colors"
              >
                <span className="material-symbols-outlined text-lg">close</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-200 mx-1"></div>
            <button 
              type="button" 
              onClick={handleGetLocation}
              className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all"
            >
              <span className="material-symbols-outlined text-[20px]">my_location</span>
            </button>
          </form>

          {/* Autocomplete Results */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-white/80 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2 duration-300">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Suggestions</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Powered by Google</span>
              </div>
              {searchResults.map((result, i) => (
                <button 
                  key={i}
                  type="button"
                  onClick={() => selectResult(result)}
                  className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 transition-colors text-left group"
                >
                  <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors shrink-0">
                    <span className="material-symbols-outlined text-[20px]">location_on</span>
                  </div>
                  <div className="flex flex-col min-w-0">
                    <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                    <span className="text-[10px] text-slate-400 truncate mt-0.5">Verified Location</span>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      </header>

      {/* Main Dashboard Content */}
      <div className={`flex-1 flex flex-col items-center py-8 transition-opacity duration-300 ${showResults ? 'opacity-20 pointer-events-none' : 'opacity-100'}`}>
        <div className="relative flex items-center justify-center">
          <div className={`w-64 h-64 rounded-full border-[12px] border-white/80 shadow-inner flex items-center justify-center relative bg-white/20 transition-all duration-700 ${isLocating ? 'opacity-30 scale-95 blur-sm' : 'opacity-100'}`}>
            <svg className="absolute inset-0 w-full h-full -rotate-90" viewBox="0 0 100 100">
              <circle 
                style={{ color: aqiStrokeColor }}
                cx="50" cy="50" r="44" 
                fill="transparent" 
                stroke="currentColor" 
                strokeDasharray="276" 
                strokeDashoffset={276 - (276 * Math.min(location.aqi, 200) / 200)} 
                strokeLinecap="round" 
                strokeWidth="8" 
                className="transition-all duration-1000 ease-out"
              />
            </svg>
            <div className="flex flex-col items-center text-center z-10">
              <span className="text-slate-500 text-[10px] font-bold tracking-tighter uppercase mb-1">Current AQI</span>
              <h1 className="text-pale-blue-900 text-7xl font-extrabold tracking-tight">{location.aqi}</h1>
              <div className={`mt-2 px-4 py-1.5 rounded-full border shadow-sm transition-colors ${aqiBgClass} ${aqiBorderClass}`}>
                <p className={`text-sm font-bold ${aqiColorClass}`}>{aqiLabel}</p>
              </div>
            </div>
          </div>
          {isLocating && (
             <div className="absolute inset-0 flex items-center justify-center">
                <span className="material-symbols-outlined text-primary text-5xl animate-spin">sync</span>
             </div>
          )}
        </div>

        {/* Mini Stats */}
        <div className={`grid grid-cols-2 gap-4 w-full mt-8 transition-all duration-500 ${isLocating ? 'opacity-50 blur-[2px]' : ''}`}>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">filter_alt</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">PM 2.5</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.pm25}</span>
              <span className="text-[10px] text-slate-400 font-medium">g/m</span>
            </div>
          </div>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">co2</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">NO2</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.no2}</span>
              <span className="text-[10px] text-slate-400 font-medium">ppb</span>
            </div>
          </div>
        </div>

        {/* Daily Impact Card */}
        <div className="w-full bg-white/40 border border-white/60 backdrop-blur-sm p-5 rounded-3xl mt-6 shadow-sm">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h3 className="text-pale-blue-900 font-bold text-lg leading-tight">Your Daily Impact</h3>
              <p className="text-primary text-[10px] font-medium mt-1">Based on local transport & air data</p>
            </div>
            <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
              <span className="material-symbols-outlined text-primary">eco</span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <div className="flex flex-col">
              <span className="text-2xl font-bold text-slate-800">2.4<span className="text-sm font-normal text-slate-400 ml-1">kg CO2</span></span>
              <span className="text-[10px] text-emerald-600 font-bold tracking-wider mt-1">
                <span className="material-symbols-outlined text-[10px] align-middle mr-1">trending_down</span>
                -12% FROM YESTERDAY
              </span>
            </div>
            <button 
              onClick={onViewImpact}
              className="bg-primary text-white shadow-md shadow-primary/20 px-4 py-2 rounded-xl text-[10px] font-bold hover:bg-primary/90 transition-all"
            >
              VIEW TIPS
            </button>
          </div>
        </div>

        {/* Emergency Button */}
        <div className="w-full mt-auto pt-8">
          <button 
            onClick={onShowEmergency}
            className="relative w-full group overflow-hidden"
          >
            <div className="absolute inset-0 bg-aqi-severe/10 rounded-2xl"></div>
            <div className="relative bg-white hover:bg-slate-50 transition-colors py-4 rounded-2xl flex items-center justify-center gap-3 shadow-sm border border-aqi-severe/20">
              <span className="material-symbols-outlined text-aqi-severe">warning</span>
              <span className="text-slate-800 font-bold uppercase tracking-widest text-sm">Emergency Protocols</span>
            </div>
          </button>
          <p className="text-center text-[10px] text-slate-400 mt-3 italic">"Safety instructions available for severe conditions"</p>
        </div>
      </div>
    </div>
  );
};

