import React, { useState } from 'react';
import { ImpactData, LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

interface CalculatorProps {
  currentLocationName: string;
  initialData: ImpactData;
  onCalculate: (data: ImpactData) => void;
  onLocationChange?: (loc: LocationData) => void; // Optional callback to update global location
}

interface SearchResult {
  title: string;
  uri: string;
}

const Calculator: React.FC<CalculatorProps> = ({ currentLocationName, initialData, onCalculate, onLocationChange }) => {
  const [data, setData] = useState<ImpactData>(initialData);
  const [searchQuery, setSearchQuery] = useState(currentLocationName);
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  const adjustAC = (val: number) => {
    setData(prev => ({ ...prev, acUnits: Math.max(0, prev.acUnits + val) }));
  };

  const handleGetLocation = () => {
    if (!navigator.geolocation) return;
    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        const newLoc: LocationData = {
          name: `Current Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
          lat: latitude,
          lng: longitude,
          aqi: 45,
          pm25: 10,
          no2: 5
        };
        setSearchQuery(newLoc.name);
        onLocationChange?.(newLoc);
        setIsLocating(false);
        setShowResults(false);
      },
      () => setIsLocating(false)
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-3-flash-preview",
        contents: `Find precise coordinates and location details for: "${searchQuery}". Format answer as: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({ title: c.maps.title || "Found Location", uri: c.maps.uri }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch && !showResults) {
        onLocationChange?.({
          name: searchQuery,
          lat: parseFloat(coordMatch[1]),
          lng: parseFloat(coordMatch[2]),
          aqi: 50, pm25: 12, no2: 8
        });
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const selectResult = (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    onLocationChange?.({
      name: result.title,
      aqi: 50, pm25: 12, no2: 8
    });
  };

  return (
    <div className="min-h-screen bg-slate-50/50 p-4 pt-8 pb-32">
      <div className="mb-8">
        <p className="text-primary font-black text-[10px] uppercase tracking-[0.2em] mb-2 opacity-60">Analytics Hub</p>
        <h1 className="text-3xl font-black text-slate-900 tracking-tight">Impact Calculator</h1>
      </div>

      {/* Google-Style Location Search Bar */}
      <section className="mb-10">
        <div className="flex items-center gap-2 mb-4 px-1">
          <span className="material-symbols-outlined text-primary text-sm">location_on</span>
          <h3 className="text-slate-900 text-sm font-black uppercase tracking-wider">Analysis Context</h3>
        </div>

        <div className="relative z-50">
          <form onSubmit={handleSearch} className="flex items-center bg-white border border-slate-100 shadow-xl shadow-slate-200/50 rounded-full p-1.5 pr-3 transition-all focus-within:ring-4 focus-within:ring-primary/10">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                {isLocating ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-bold text-slate-800 placeholder:text-slate-400 py-2.5" 
              placeholder="Change location..." 
              type="text" 
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery !== currentLocationName && (
              <button type="button" onClick={() => { setSearchQuery(currentLocationName); setShowResults(false); }} className="p-2 text-slate-300 hover:text-slate-600 transition-colors">
                <span className="material-symbols-outlined text-lg">undo</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-100 mx-1"></div>
            <button type="button" onClick={handleGetLocation} className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all">
              <span className="material-symbols-outlined text-[20px]">my_location</span>
            </button>
          </form>

          {/* Autocomplete Results */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-slate-100 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[9px] font-black text-slate-400 uppercase tracking-widest">Select Location</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Powered by Google</span>
              </div>
              {searchResults.map((result, i) => (
                <button key={i} onClick={() => selectResult(result)} className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 text-left group">
                  <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors">
                    <span className="material-symbols-outlined text-[20px]">location_on</span>
                  </div>
                  <div className="flex flex-col min-w-0">
                    <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                    <span className="text-[10px] text-slate-400 truncate mt-0.5 tracking-tight">Verified by Google Maps</span>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
        <p className="text-[10px] text-slate-400 mt-4 px-5 font-medium leading-relaxed">
          The calculator adapts its base emission factors based on your region's local energy mix and environmental standards.
        </p>
      </section>

      {/* Emission Data Inputs */}
      <section className="mb-8">
        <div className="flex items-center gap-2 mb-4 px-1">
          <span className="material-symbols-outlined text-primary text-sm">co2</span>
          <h3 className="text-slate-900 text-sm font-black uppercase tracking-wider">Air Quality Metrics</h3>
        </div>
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-white p-2 rounded-[1.5rem] border border-slate-100 shadow-sm">
            <p className="text-slate-400 text-[10px] font-black uppercase tracking-tighter pb-1 px-3 mt-1">CO2 Rate</p>
            <input 
              className="w-full rounded-xl border-none bg-transparent h-12 px-3 text-lg font-black text-slate-800 focus:ring-0" 
              placeholder="0.0" 
              type="number"
              value={data.co2Rate || ''}
              onChange={e => setData(prev => ({ ...prev, co2Rate: parseFloat(e.target.value) || 0 }))}
            />
          </div>
          <div className="bg-white p-2 rounded-[1.5rem] border border-slate-100 shadow-sm">
            <p className="text-slate-400 text-[10px] font-black uppercase tracking-tighter pb-1 px-3 mt-1">Gas Level</p>
            <input 
              className="w-full rounded-xl border-none bg-transparent h-12 px-3 text-lg font-black text-slate-800 focus:ring-0" 
              placeholder="0" 
              type="number"
              value={data.gasLevels || ''}
              onChange={e => setData(prev => ({ ...prev, gasLevels: parseFloat(e.target.value) || 0 }))}
            />
          </div>
