import React, { useState, useEffect } from 'react';
import { Screen, ImpactData, FootprintResult, LocationData } from './types';
import Dashboard from './screens/Dashboard';
import Calculator from './screens/Calculator';
import Results from './screens/Results';
import Settings from './screens/Settings';
import MapScreen from './screens/MapScreen';
import EmergencyOverlay from './screens/EmergencyOverlay';
import BottomNav from './components/BottomNav';

const App: React.FC = () => {
  const [currentScreen, setCurrentScreen] = useState<Screen>(Screen.DASHBOARD);
  const [showEmergency, setShowEmergency] = useState(false);
  const [location, setLocation] = useState<LocationData>({
    name: 'San Francisco, CA',
    aqi: 42,
    pm25: 12.4,
    no2: 8.2
  });

  const [impactData, setImpactData] = useState<ImpactData>({
    acUnits: 2,
    fanUsage: 8,
    energySource: 'Main Grid (Coal/Gas)',
    co2Rate: 0,
    gasLevels: 0
  });

  const [results, setResults] = useState<FootprintResult | null>(null);

  const handleCalculate = (data: ImpactData) => {
    setImpactData(data);
    // Mock calculation logic
    const total = 2.5 + (data.acUnits * 0.3) + (data.fanUsage * 0.05);
    setResults({
      totalTons: parseFloat(total.toFixed(1)),
      breakdown: {
        acUsage: 40,
        gasEmissions: 35,
        energySource: 25
      }
    });
    setCurrentScreen(Screen.RESULTS);
  };

  const handleLocationChange = (newLocation: LocationData) => {
    setLocation(newLocation);
  };

  const renderScreen = () => {
    switch (currentScreen) {
      case Screen.DASHBOARD:
        return <Dashboard 
          location={location}
          onLocationChange={handleLocationChange}
          onShowEmergency={() => setShowEmergency(true)} 
          onViewImpact={() => setCurrentScreen(Screen.RESULTS)}
        />;
      case Screen.MAP:
        return <MapScreen 
          currentLocation={location}
          onLocationSelect={(newLoc) => {
            handleLocationChange(newLoc);
            setCurrentScreen(Screen.DASHBOARD);
          }}
        />;
      case Screen.CALCULATOR:
        return <Calculator 
          currentLocationName={location.name}
          initialData={impactData} 
          onCalculate={handleCalculate}
          onLocationChange={handleLocationChange}
        />;
      case Screen.RESULTS:
        return <Results result={results} onBack={() => setCurrentScreen(Screen.DASHBOARD)} />;
      case Screen.SETTINGS:
        return <Settings />;
      default:
        return <Dashboard 
          location={location}
          onLocationChange={handleLocationChange}
          onShowEmergency={() => setShowEmergency(true)} 
          onViewImpact={() => setCurrentScreen(Screen.RESULTS)} 
        />;
    }
  };

  return (
    <div className="relative flex flex-col min-h-screen bg-pale-blue-50 max-w-md mx-auto shadow-2xl overflow-hidden">
      <main className="flex-1 overflow-y-auto hide-scrollbar pb-20">
        {renderScreen()}
      </main>

      {currentScreen !== Screen.RESULTS && (
        <BottomNav 
          currentScreen={currentScreen} 
          onNavigate={setCurrentScreen} 
        />
      )}

      {showEmergency && (
        <EmergencyOverlay onClose={() => setShowEmergency(false)} />
      )}
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { ImpactData, LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

interface CalculatorProps {
  currentLocationName: string;
  initialData: ImpactData;
  onCalculate: (data: ImpactData) => void;
  onLocationChange?: (loc: LocationData) => void;
}

interface SearchResult {
  title: string;
  uri: string;
  ecoIntensity?: string;
  co2Factor?: number;
}

const Calculator: React.FC<CalculatorProps> = ({ currentLocationName, initialData, onCalculate, onLocationChange }) => {
  const [data, setData] = useState<ImpactData>(initialData);
  const [searchQuery, setSearchQuery] = useState(currentLocationName);
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [activeProfile, setActiveProfile] = useState<string | null>(null);

  const adjustAC = (val: number) => {
    setData(prev => ({ ...prev, acUnits: Math.max(0, prev.acUnits + val) }));
  };

  const handleGetLocation = () => {
    if (!navigator.geolocation) return;
    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        const newLoc: LocationData = {
          name: `My Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
          lat: latitude,
          lng: longitude,
          aqi: 45,
          pm25: 10,
          no2: 5
        };
        setSearchQuery(newLoc.name);
        onLocationChange?.(newLoc);
        setIsLocating(false);
        setShowResults(false);
        setActiveProfile("Local GPS Context Applied");
      },
      () => setIsLocating(false)
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-3-flash-preview",
        contents: `Find precise coordinates and environmental context for: "${searchQuery}". 
                   Determine the typical carbon intensity of the local power grid (gCO2/kWh).
                   Format exactly: "LOCATION: [Name] | COORDS: [LAT, LNG] | INTENSITY: [Value] | PROFILE: [Eco Level]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];

      // Parse emission factors from the response text
      const co2Match = response.text.match(/INTENSITY:\s*(\d+)/i);
      const profileMatch = response.text.match(/PROFILE:\s*([^|]+)/i);
      const intensityValue = co2Match ? parseInt(co2Match[1]) / 1000 : 0.45; // Default 0.45 if failed
      const ecoLabel = profileMatch ? profileMatch[1].trim() : "Standard Grid";

      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({ 
          title: c.maps.title || "Found Location", 
          uri: c.maps.uri,
          ecoIntensity: ecoLabel,
          co2Factor: intensityValue
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch && !showResults) {
        applyLocationContext(searchQuery, parseFloat(coordMatch[1]), parseFloat(coordMatch[2]), intensityValue, ecoLabel);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const applyLocationContext = (name: string, lat: number, lng: number, co2Factor: number, profile: string) => {
    onLocationChange?.({
      name,
      lat,
      lng,
      aqi: 50, pm25: 12, no2: 8
    });
    setData(prev => ({ ...prev, co2Rate: co2Factor }));
    setActiveProfile(profile);
  };

  const selectResult = (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    applyLocationContext(result.title, 0, 0, result.co2Factor || 0.45, result.ecoIntensity || "Regional Profile");
  };

  return (
    <div className="min-h-screen bg-slate-50/50 p-4 pt-8 pb-32">
      <div className="mb-8">
        <p className="text-primary font-black text-[10px] uppercase tracking-[0.2em] mb-2 opacity-60">Analytics Hub</p>
        <h1 className="text-3xl font-black text-slate-900 tracking-tight">Impact Calculator</h1>
      </div>

      {/* Google-Style Location Search Bar with Carbon Intel */}
      <section className="mb-10">
        <div className="flex items-center justify-between mb-4 px-1">
          <div className="flex items-center gap-2">
            <span className="material-symbols-outlined text-primary text-sm">location_on</span>
            <h3 className="text-slate-900 text-sm font-black uppercase tracking-wider">Analysis Context</h3>
          </div>
          {activeProfile && (
            <div className="flex items-center gap-1.5 bg-emerald-50 border border-emerald-100 px-2 py-0.5 rounded-lg animate-in fade-in slide-in-from-right-2">
              <span className="material-symbols-outlined text-[12px] text-emerald-600">eco</span>
              <span className="text-[9px] font-black text-emerald-600 uppercase">{activeProfile}</span>
            </div>
          )}
        </div>

        <div className="relative z-50">
          <form onSubmit={handleSearch} className="flex items-center bg-white border border-slate-100 shadow-xl shadow-slate-200/50 rounded-full p-1.5 pr-3 transition-all focus-within:ring-4 focus-within:ring-primary/10">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                {isLocating ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-bold text-slate-800 placeholder:text-slate-400 py-2.5" 
              placeholder="Search region for carbon data..." 
              type="text" 
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery !== currentLocationName && (
              <button type="button" onClick={() => { setSearchQuery(currentLocationName); setShowResults(false); setActiveProfile(null); }} className="p-2 text-slate-300 hover:text-slate-600 transition-colors">
                <span className="material-symbols-outlined text-lg">undo</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-100 mx-1"></div>
            <button type="button" onClick={handleGetLocation} className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all">
              <span className="material-symbols-outlined text-[20px]">my_location</span>
            </button>
          </form>

          {/* Autocomplete Results with Eco Snippets */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-slate-100 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[9px] font-black text-slate-400 uppercase tracking-widest">Global Locations</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Grounding Active</span>
              </div>
              {searchResults.map((result, i) => (
                <button key={i} onClick={() => selectResult(result)} className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 text-left group">
                  <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors">
                    <span className="material-symbols-outlined text-[20px]">location_on</span>
                  </div>
                  <div className="flex flex-col min-w-0 flex-1">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                      <span className="text-[9px] font-black text-emerald-600 bg-emerald-50 px-1.5 py-0.5 rounded uppercase">
                        {result.ecoIntensity || 'Calculating...'}
                      </span>
                    </div>
                    <span className="text-[10px] text-slate-400 truncate mt-0.5 tracking-tight flex items-center gap-1">
                       <span className="material-symbols-outlined text-[10px]">energy_savings_leaf</span>
                       Pulling local CO2 emission factors
                    </span>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
        <p className="text-[10px] text-slate-400 mt-4 px-5 font-medium leading-relaxed">
          Select a location to automatically pull its <span className="text-slate-600 font-bold">Grid Emission Factor</span>. This determines how many tons of CO2 are produced per unit of energy used.
        </p>
      </section>

      {/* Emission Data Inputs */}
      <section className="mb-8">
        <div className="flex items-center gap-2 mb-4 px-1">
          <span className="material-symbols-outlined text-primary text-sm">co2</span>
          <h3 className="text-slate-900 text-sm font-black uppercase tracking-wider">Emission Coefficients</h3>
        </div>
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-white p-2 rounded-[1.5rem] border border-slate-100 shadow-sm relative overflow-hidden group">
            {activeProfile && (
               <div className="absolute top-0 right-0 p-1">
                 <div className="size-2 rounded-full bg-emerald-400 animate-pulse"></div>
               </div>
            )}
            <p className="text-slate-400 text-[10px] font-black uppercase tracking-tighter pb-1 px-3 mt-1">Grid kgCO2/kWh</p>
            <input 
              className="w-full rounded-xl border-none bg-transparent h-12 px-3 text-lg font-black text-slate-800 focus:ring-0" 
              placeholder="0.0" 
              type="number"
              step="0.01"
              value={data.co2Rate || ''}
              onChange={e => setData(prev => ({ ...prev, co2Rate: parseFloat(e.target.value) || 0 }))}
            />
          </div>
          <div className="bg-white p-2 rounded-[1.5rem] border border-slate-100 shadow-sm">
            <p className="text-slate-400 text-[10px] font-black uppercase tracking-tighter pb-1 px-3 mt-1">Daily Gas Unit</p>
            <input 
              className="w-full rounded-xl border-none bg-transparent h-12 px-3 text-lg font-black text-slate-800 focus:ring-0" 
              placeholder="0" 
              type="number"
              value={data.gasLevels || ''}
              onChange={e => setData(prev => ({ ...prev, gasLevels: parseFloat(e.target.value) || 0 }))}
            />
          </div>
        </div>
      </section>

      {/* Energy Consumption Section */}
      <section className="mb-8 p-6 rounded-[2.5rem] bg-white border border-slate-100 shadow-xl shadow-slate-200/40">
        <div className="flex items-center gap-2 mb-6">
          <div className="size-8 rounded-xl bg-primary/10 flex items-center justify-center text-primary">
