import React, { useState, useEffect, useRef } from 'react';
import { ImpactData, LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

// Declare L for Leaflet global
declare const L: any;

interface CalculatorProps {
  currentLocationName: string;
  initialData: ImpactData;
  onCalculate: (data: ImpactData) => void;
  onLocationChange?: (loc: LocationData) => void;
}

interface SearchResult {
  title: string;
  uri: string;
  ecoIntensity?: string;
  co2Factor?: number;
  lat?: number;
  lng?: number;
}

const Calculator: React.FC<CalculatorProps> = ({ currentLocationName, initialData, onCalculate, onLocationChange }) => {
  const [data, setData] = useState<ImpactData>(initialData);
  const [searchQuery, setSearchQuery] = useState(currentLocationName);
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [activeProfile, setActiveProfile] = useState<string | null>(null);

  const mapRef = useRef<any>(null);
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const markerRef = useRef<any>(null);

  // Initialize mini-map
  useEffect(() => {
    if (!mapContainerRef.current || mapRef.current) return;

    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false,
      dragging: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      touchZoom: false
    }).setView([37.7749, -122.4194], 12);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    markerRef.current = L.circleMarker([37.7749, -122.4194], {
      radius: 8,
      fillColor: "#3b82f6",
      color: "#fff",
      weight: 3,
      opacity: 1,
      fillOpacity: 0.8
    }).addTo(mapRef.current);

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, []);

  const adjustAC = (val: number) => {
    setData(prev => ({ ...prev, acUnits: Math.max(0, prev.acUnits + val) }));
  };

  const updateMap = (lat: number, lng: number) => {
    if (mapRef.current) {
      mapRef.current.flyTo([lat, lng], 13, { duration: 1.5 });
      if (markerRef.current) {
        markerRef.current.setLatLng([lat, lng]);
      }
    }
  };

  const handleGetLocation = () => {
    if (!navigator.geolocation) return;
    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        const newLoc: LocationData = {
          name: `My Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
          lat: latitude,
          lng: longitude,
          aqi: 45,
          pm25: 10,
          no2: 5
        };
        setSearchQuery(newLoc.name);
        onLocationChange?.(newLoc);
        updateMap(latitude, longitude);
        setIsLocating(false);
        setShowResults(false);
        setActiveProfile("Local GPS Context Applied");
      },
      () => setIsLocating(false)
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-3-flash-preview",
        contents: `Find precise coordinates and environmental context for: "${searchQuery}". 
                   Determine the typical carbon intensity of the local power grid (gCO2/kWh).
                   Format exactly: "LOCATION: [Name] | COORDS: [LAT, LNG] | INTENSITY: [Value] | PROFILE: [Eco Level]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];

      const co2Match = response.text.match(/INTENSITY:\s*(\d+)/i);
      const profileMatch = response.text.match(/PROFILE:\s*([^|]+)/i);
      const intensityValue = co2Match ? parseInt(co2Match[1]) / 1000 : 0.45;
      const ecoLabel = profileMatch ? profileMatch[1].trim() : "Standard Grid";

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      let lat = 0, lng = 0;
      if (coordMatch) {
        lat = parseFloat(coordMatch[1]);
        lng = parseFloat(coordMatch[2]);
      }

      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({ 
          title: c.maps.title || "Found Location", 
          uri: c.maps.uri,
          ecoIntensity: ecoLabel,
          co2Factor: intensityValue,
          lat, lng
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      if (coordMatch && !showResults) {
        applyLocationContext(searchQuery, lat, lng, intensityValue, ecoLabel);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const applyLocationContext = (name: string, lat: number, lng: number, co2Factor: number, profile: string) => {
    onLocationChange?.({
      name,
      lat,
      lng,
      aqi: 50, pm25: 12, no2: 8
    });
    if (lat !== 0 && lng !== 0) updateMap(lat, lng);
    setData(prev => ({ ...prev, co2Rate: co2Factor }));
    setActiveProfile(profile);
  };

  const selectResult = (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    applyLocationContext(result.title, result.lat || 0, result.lng || 0, result.co2Factor || 0.45, result.ecoIntensity || "Regional Profile");
