export default Calculator;

import React, { useState, useEffect, useRef } from 'react';
import { ImpactData, LocationData, Screen } from '../types';
import { GoogleGenAI } from "@google/genai";

// Declare L for Leaflet global
declare const L: any;

interface CalculatorProps {
  currentLocationName: string;
  initialData: ImpactData;
  onCalculate: (data: ImpactData) => void;
  onLocationChange?: (loc: LocationData) => void;
  onNavigateToMap?: () => void;
}

interface SearchResult {
  title: string;
  uri: string;
  ecoIntensity?: string;
  co2Factor?: number;
  lat?: number;
  lng?: number;
}

const Calculator: React.FC<CalculatorProps> = ({ 
  currentLocationName, 
  initialData, 
  onCalculate, 
  onLocationChange,
  onNavigateToMap 
}) => {
  const [data, setData] = useState<ImpactData>(initialData);
  const [searchQuery, setSearchQuery] = useState(currentLocationName);
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [activeProfile, setActiveProfile] = useState<string | null>(null);

  const mapRef = useRef<any>(null);
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const markerRef = useRef<any>(null);

  // Initialize mini-map
  useEffect(() => {
    if (!mapContainerRef.current || mapRef.current) return;

    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false,
      dragging: true,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      touchZoom: true
    }).setView([37.7749, -122.4194], 12);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    markerRef.current = L.circleMarker([37.7749, -122.4194], {
      radius: 10,
      fillColor: "#3b82f6",
      color: "#fff",
      weight: 3,
      opacity: 1,
      fillOpacity: 0.8
    }).addTo(mapRef.current);

    // Allow user to pick location by clicking map directly
    mapRef.current.on('click', (e: any) => {
      const { lat, lng } = e.latlng;
      applyLocationContext(`Map Point (${lat.toFixed(2)}, ${lng.toFixed(2)})`, lat, lng, 0.45, "Custom Point");
    });

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, []);

  const adjustAC = (val: number) => {
    setData(prev => ({ ...prev, acUnits: Math.max(0, prev.acUnits + val) }));
  };

  const updateMap = (lat: number, lng: number) => {
    if (mapRef.current) {
      mapRef.current.flyTo([lat, lng], 14, { duration: 1.5 });
      if (markerRef.current) {
        markerRef.current.setLatLng([lat, lng]);
      }
    }
  };

  const handleGetLocation = () => {
    if (!navigator.geolocation) return;
    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        applyLocationContext(`My Current Location`, latitude, longitude, 0.45, "Active GPS");
        setIsLocating(false);
        setShowResults(false);
      },
      () => setIsLocating(false)
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-3-flash-preview",
        contents: `Find precise coordinates and environmental context for: "${searchQuery}". 
                   Determine the typical carbon intensity of the local power grid (gCO2/kWh).
                   Format exactly: "LOCATION: [Name] | COORDS: [LAT, LNG] | INTENSITY: [Value] | PROFILE: [Eco Level]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];

      const co2Match = response.text.match(/INTENSITY:\s*(\d+)/i);
      const profileMatch = response.text.match(/PROFILE:\s*([^|]+)/i);
      const intensityValue = co2Match ? parseInt(co2Match[1]) / 1000 : 0.45;
      const ecoLabel = profileMatch ? profileMatch[1].trim() : "Standard Grid";

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      let lat = 0, lng = 0;
      if (coordMatch) {
        lat = parseFloat(coordMatch[1]);
        lng = parseFloat(coordMatch[2]);
      }

      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({ 
          title: c.maps.title || "Found Location", 
          uri: c.maps.uri,
          ecoIntensity: ecoLabel,
          co2Factor: intensityValue,
          lat, lng
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      if (coordMatch && !showResults) {
        applyLocationContext(searchQuery, lat, lng, intensityValue, ecoLabel);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const applyLocationContext = (name: string, lat: number, lng: number, co2Factor: number, profile: string) => {
    setSearchQuery(name);
    onLocationChange?.({
      name,
      lat,
      lng,
      aqi: 50, pm25: 12, no2: 8
    });
    if (lat !== 0 && lng !== 0) updateMap(lat, lng);
    setData(prev => ({ ...prev, co2Rate: co2Factor }));
    setActiveProfile(profile);
  };

  const selectResult = (result: SearchResult) => {
    setShowResults(false);
    applyLocationContext(result.title, result.lat || 0, result.lng || 0, result.co2Factor || 0.45, result.ecoIntensity || "Regional Profile");
  };

  return (
    <div className="min-h-screen bg-slate-50/50 pb-32">
      {/* Top Status Header */}
      <header className="sticky top-0 z-[100] bg-white/80 backdrop-blur-2xl border-b border-slate-100 px-6 py-4 flex items-center justify-between">
        <div className="flex flex-col">
          <span className="text-[9px] font-black text-primary uppercase tracking-[0.2em] leading-none mb-1">Impact Tracking</span>
          <div className="flex items-center gap-1.5">
            <span className="material-symbols-outlined text-primary text-sm">satellite_alt</span>
            <h2 className="text-sm font-black text-slate-800 tracking-tight truncate max-w-[150px]">{currentLocationName}</h2>
          </div>
        </div>
        <div className="flex items-center gap-2">
           <button 
             onClick={onNavigateToMap}
             className="size-10 rounded-full bg-slate-50 flex items-center justify-center text-slate-400 hover:text-primary hover:bg-primary/5 transition-all"
           >
             <span className="material-symbols-outlined text-xl">map</span>
           </button>
           <button className="size-10 rounded-full bg-slate-900 flex items-center justify-center text-white shadow-lg shadow-slate-900/20">
             <span className="material-symbols-outlined text-xl">analytics</span>
           </button>
        </div>
      </header>

      <div className="p-4 pt-6">
        {/* Google-Style Location Search Bar */}
        <section className="mb-8">
          <div className="flex items-center justify-between mb-4 px-1">
            <div className="flex items-center gap-2">
              <span className="material-symbols-outlined text-primary text-sm">location_searching</span>
              <h3 className="text-slate-900 text-sm font-black uppercase tracking-wider">Region Selection</h3>
            </div>
            {activeProfile && (
              <div className="flex items-center gap-1.5 bg-emerald-50 border border-emerald-100 px-2 py-0.5 rounded-lg animate-in fade-in slide-in-from-right-2">
                <span className="material-symbols-outlined text-[12px] text-emerald-600">verified</span>
                <span className="text-[9px] font-black text-emerald-600 uppercase">{activeProfile}</span>
              </div>
            )}
          </div>

          <div className="relative z-[90] mb-4">
            <form onSubmit={handleSearch} className="flex items-center bg-white border border-slate-100 shadow-xl shadow-slate-200/50 rounded-2xl p-1.5 pr-3 transition-all focus-within:ring-4 focus-within:ring-primary/10">
              <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
                <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                  {isLocating ? 'sync' : 'search'}
                </span>
              </div>
              <input 
                className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-bold text-slate-800 placeholder:text-slate-400 py-2.5" 
                placeholder="Find location for carbon tracking..." 
                type="text" 
                value={searchQuery}
                onFocus={() => searchResults.length > 0 && setShowResults(true)}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <div className="flex items-center gap-1">
                {searchQuery !== currentLocationName && (
                  <button type="button" onClick={() => { setSearchQuery(currentLocationName); setShowResults(false); setActiveProfile(null); }} className="p-2 text-slate-300 hover:text-slate-600">
                    <span className="material-symbols-outlined text-lg">close</span>
                  </button>
                )}
                <div className="w-[1px] h-6 bg-slate-100 mx-1"></div>
                <button type="button" onClick={handleGetLocation} className="p-2.5 rounded-xl text-primary hover:bg-primary/5 transition-all">
                  <span className="material-symbols-outlined text-[20px]">my_location</span>
                </button>
              </div>
            </form>

            {/* Autocomplete Results */}
            {showResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[1.5rem] shadow-2xl border border-slate-100 overflow-hidden py-2 animate-in fade-in slide-in-from-top-2">
                {searchResults.map((result, i) => (
                  <button key={i} onClick={() => selectResult(result)} className="w-full px-5 py-3 flex items-start gap-4 hover:bg-slate-50 text-left group">
                    <div className="size-9 rounded-xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors">
                      <span className="material-symbols-outlined text-lg">location_on</span>
                    </div>
                    <div className="flex flex-col min-w-0 flex-1 py-1">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                        <span className="text-[8px] font-black text-emerald-600 bg-emerald-50 px-1 rounded uppercase">{result.ecoIntensity}</span>
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Interactive Tracker Map in Location Section */}
          <div className="relative w-full h-44 bg-slate-200 rounded-[2rem] overflow-hidden border border-white shadow-xl shadow-slate-200/50 group">
            <div ref={mapContainerRef} className="w-full h-full grayscale-[0.2] contrast-[0.9]" />
            <div className="absolute inset-0 bg-gradient-to-t from-slate-900/60 via-transparent to-transparent pointer-events-none" />
            <div className="absolute bottom-4 left-6 flex flex-col">
               <div className="flex items-center gap-2 mb-1">
                 <div className="size-2 rounded-full bg-blue-400 animate-ping"></div>
