export default MapScreen;

import React, { useState } from 'react';
import { LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";

interface DashboardProps {
  location: LocationData;
  onLocationChange: (loc: LocationData) => void;
  onShowEmergency: () => void;
  onViewImpact: () => void;
}

interface SearchResult {
  title: string;
  uri: string;
}

const Dashboard: React.FC<DashboardProps> = ({ location, onLocationChange, onShowEmergency, onViewImpact }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  const handleGetLocation = () => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        // Simulating data for current location
        setTimeout(() => {
          onLocationChange({
            name: `My Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
            lat: latitude,
            lng: longitude,
            aqi: Math.floor(Math.random() * 60) + 10,
            pm25: parseFloat((Math.random() * 15 + 5).toFixed(1)),
            no2: parseFloat((Math.random() * 10 + 2).toFixed(1))
          });
          setIsLocating(false);
          setSearchQuery('');
          setShowResults(false);
        }, 1500);
      },
      (error) => {
        setIsLocating(false);
        alert('Unable to retrieve your location. ' + error.message);
      }
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash-latest",
        contents: `Find precise coordinates and location details for: "${searchQuery}". 
                   If there are multiple matches, list them. 
                   Format answer as: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: {
          tools: [{ googleMaps: {} }],
        },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Found Location",
          uri: c.maps.uri
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        onLocationChange({
          name: searchQuery,
          lat,
          lng,
          aqi: Math.floor(Math.random() * 250) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
        if (extractedResults.length === 0) setShowResults(false);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsLocating(true);

    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        onLocationChange({
          name: result.title,
          lat: parseFloat(geoData[0].lat),
          lng: parseFloat(geoData[0].lon),
          aqi: Math.floor(Math.random() * 250) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
      }
    } catch (e) {
      console.error(e);
    } finally {
      setIsLocating(false);
    }
  };

  const aqiColorClass = location.aqi <= 50 ? 'text-aqi-good' : 
                        location.aqi <= 100 ? 'text-aqi-moderate' : 
                        location.aqi <= 200 ? 'text-aqi-unhealthy' : 'text-aqi-severe';

  const aqiBgClass = location.aqi <= 50 ? 'bg-aqi-good/20' : 
                     location.aqi <= 100 ? 'bg-aqi-moderate/20' : 
                     location.aqi <= 200 ? 'bg-aqi-unhealthy/20' : 'bg-aqi-severe/20';

  const aqiBorderClass = location.aqi <= 50 ? 'border-aqi-good/30' : 
                         location.aqi <= 100 ? 'border-aqi-moderate/30' : 
                         location.aqi <= 200 ? 'border-aqi-unhealthy/30' : 'border-aqi-severe/30';

  const aqiLabel = location.aqi <= 50 ? 'Healthy' : 
                   location.aqi <= 100 ? 'Moderate' : 
                   location.aqi <= 200 ? 'Unhealthy' : 'Hazardous';

  const aqiStrokeColor = location.aqi <= 50 ? '#13ec5b' : 
                         location.aqi <= 100 ? '#fde047' : 
                         location.aqi <= 200 ? '#fb923c' : '#ef4444';

  const isHazardous = location.aqi >= 200;

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-[#e0effe] to-[#f0f7ff] p-4 relative">
      {/* Dynamic Hazardous Banner */}
      {isHazardous && (
        <div 
          onClick={onShowEmergency}
          className="fixed top-0 left-0 right-0 z-[200] bg-red-600 text-white px-4 py-3 flex items-center justify-between shadow-lg animate-in slide-in-from-top duration-500 cursor-pointer"
        >
          <div className="flex items-center gap-3">
            <span className="material-symbols-outlined fill-icon animate-pulse">report</span>
            <div className="flex flex-col">
              <span className="text-[10px] font-black uppercase tracking-[0.2em] leading-none mb-1 opacity-80">Severe Alert</span>
              <span className="text-xs font-bold">Hazardous levels detected nearby!</span>
            </div>
          </div>
          <span className="material-symbols-outlined text-sm">chevron_right</span>
        </div>
      )}

      {/* Header with Google-Style Search */}
      <header className={`flex flex-col pt-6 pb-2 gap-4 relative z-[100] transition-all duration-500 ${isHazardous ? 'mt-12' : ''}`}>
        <div className="flex items-center justify-between px-1">
          <button className="p-2 -ml-2 text-pale-blue-900/70">
            <span className="material-symbols-outlined">menu</span>
          </button>
          <div className="flex flex-col items-center">
            <span className="text-[10px] uppercase tracking-widest text-primary font-bold">AirGuard</span>
            <div className="flex items-center gap-1">
              <span className={`material-symbols-outlined text-sm text-primary`}>location_on</span>
              <h2 className="text-pale-blue-900 text-sm font-semibold truncate max-w-[180px]">
                {location.name}
              </h2>
            </div>
          </div>
          <button className="p-2 -mr-2 text-primary">
            <span className="material-symbols-outlined text-primary">account_circle</span>
          </button>
        </div>

        <div className="relative">
          <form onSubmit={handleSearch} className="flex items-center bg-white/90 backdrop-blur-2xl rounded-full border border-white shadow-xl p-1.5 pr-3 transition-all duration-300 focus-within:ring-4 focus-within:ring-primary/10">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                {isLocating ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-semibold text-slate-800 placeholder:text-slate-400 py-2.5" 
              placeholder="Search city, landmark, or area..." 
              type="text" 
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
              <button 
                type="button" 
                onClick={() => { setSearchQuery(''); setSearchResults([]); setShowResults(false); }}
                className="p-2 text-slate-400 hover:text-slate-600 transition-colors"
              >
                <span className="material-symbols-outlined text-lg">close</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-200 mx-1"></div>
            <button 
              type="button" 
              onClick={handleGetLocation}
              className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all"
            >
              <span className="material-symbols-outlined text-[20px]">my_location</span>
            </button>
          </form>

          {/* Autocomplete Results */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-white/80 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2 duration-300">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Suggestions</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Powered by Google</span>
              </div>
              {searchResults.map((result, i) => (
                <button 
                  key={i}
                  type="button"
                  onClick={() => selectResult(result)}
                  className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 transition-colors text-left group"
                >
                  <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors shrink-0">
                    <span className="material-symbols-outlined text-[20px]">location_on</span>
                  </div>
                  <div className="flex flex-col min-w-0">
                    <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                    <span className="text-[10px] text-slate-400 truncate mt-0.5">Verified Location</span>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      </header>

      {/* Main Dashboard Content */}
      <div className={`flex-1 flex flex-col items-center py-8 transition-opacity duration-300 ${showResults ? 'opacity-20 pointer-events-none' : 'opacity-100'}`}>
        <div className="relative flex items-center justify-center">
          <div className={`w-64 h-64 rounded-full border-[12px] border-white/80 shadow-inner flex items-center justify-center relative bg-white/20 transition-all duration-700 ${isLocating ? 'opacity-30 scale-95 blur-sm' : 'opacity-100'}`}>
            <svg className="absolute inset-0 w-full h-full -rotate-90" viewBox="0 0 100 100">
              <circle 
                style={{ color: aqiStrokeColor }}
                cx="50" cy="50" r="44" 
                fill="transparent" 
                stroke="currentColor" 
                strokeDasharray="276" 
                strokeDashoffset={276 - (276 * Math.min(location.aqi, 200) / 200)} 
                strokeLinecap="round" 
                strokeWidth="8" 
                className="transition-all duration-1000 ease-out"
              />
            </svg>
            <div className="flex flex-col items-center text-center z-10">
              <span className="text-slate-500 text-[10px] font-bold tracking-tighter uppercase mb-1">Current AQI</span>
              <h1 className="text-pale-blue-900 text-7xl font-extrabold tracking-tight">{location.aqi}</h1>
              <div className={`mt-2 px-4 py-1.5 rounded-full border shadow-sm transition-colors ${aqiBgClass} ${aqiBorderClass}`}>
                <p className={`text-sm font-bold ${aqiColorClass}`}>{aqiLabel}</p>
              </div>
            </div>
          </div>
          {isLocating && (
             <div className="absolute inset-0 flex items-center justify-center">
                <span className="material-symbols-outlined text-primary text-5xl animate-spin">sync</span>
             </div>
          )}
        </div>

        {/* Mini Stats */}
        <div className={`grid grid-cols-2 gap-4 w-full mt-8 transition-all duration-500 ${isLocating ? 'opacity-50 blur-[2px]' : ''}`}>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">filter_alt</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">PM 2.5</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.pm25}</span>
              <span className="text-[10px] text-slate-400 font-medium">g/m</span>
            </div>
          </div>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">co2</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">NO2</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.no2}</span>
              <span className="text-[10px] text-slate-400 font-medium">ppb</span>
            </div>
          </div>
        </div>

        {/* Daily Impact Card */}
        <div className="w-full bg-white/40 border border-white/60 backdrop-blur-sm p-5 rounded-3xl mt-6 shadow-sm">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h3 className="text-pale-blue-900 font-bold text-lg leading-tight">Your Daily Impact</h3>
              <p className="text-primary text-[10px] font-medium mt-1">Based on local transport & air data</p>
            </div>
            <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
              <span className="material-symbols-outlined text-primary">eco</span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <div className="flex flex-col">
              <span className="text-2xl font-bold text-slate-800">2.4<span className="text-sm font-normal text-slate-400 ml-1">kg CO2</span></span>
              <span className="text-[10px] text-emerald-600 font-bold tracking-wider mt-1">
                <span className="material-symbols-outlined text-[10px] align-middle mr-1">trending_down</span>
                -12% FROM YESTERDAY
              </span>
            </div>
            <button 
              onClick={onViewImpact}
              className="bg-primary text-white shadow-md shadow-primary/20 px-4 py-2 rounded-xl text-[10px] font-bold hover:bg-primary/90 transition-all"
            >
              VIEW TIPS
            </button>
          </div>
        </div>

        {/* Emergency Button */}
        <div className="w-full mt-auto pt-8">
          <button 
            onClick={onShowEmergency}
            className={`relative w-full group overflow-hidden transition-all duration-300 ${isHazardous ? 'scale-105 shadow-xl shadow-red-500/20' : ''}`}
          >
            <div className={`absolute inset-0 ${isHazardous ? 'bg-red-600/20' : 'bg-aqi-severe/10'} rounded-2xl`}></div>
            <div className="relative bg-white hover:bg-slate-50 transition-colors py-4 rounded-2xl flex items-center justify-center gap-3 shadow-sm border border-aqi-severe/20">
              <span className={`material-symbols-outlined ${isHazardous ? 'text-red-600 animate-pulse' : 'text-aqi-severe'}`}>
                {isHazardous ? 'notification_important' : 'warning'}
              </span>
              <span className="text-slate-800 font-bold uppercase tracking-widest text-sm">
                {isHazardous ? 'Emergency active' : 'Emergency Protocols'}
              </span>
            </div>
          </button>
          <p className="text-center text-[10px] text-slate-400 mt-3 italic">
            {isHazardous ? 'Critical safety protocols engaged' : '"Safety instructions available for severe conditions"'}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState, useEffect, useRef } from 'react';
import { ImpactData, LocationData, Screen } from '../types';
import { GoogleGenAI } from "@google/genai";

// Declare L for Leaflet global
declare const L: any;

interface CalculatorProps {
  currentLocationName: string;
  initialData: ImpactData;
  onCalculate: (data: ImpactData) => void;
  onLocationChange?: (loc: LocationData) => void;
  onNavigateToMap?: () => void;
}

interface SearchResult {
  title: string;
  uri: string;
  ecoIntensity?: string;
  co2Factor?: number;
  lat?: number;
  lng?: number;
}

const Calculator: React.FC<CalculatorProps> = ({ 
  currentLocationName, 
  initialData, 
  onCalculate, 
  onLocationChange,
  onNavigateToMap 
}) => {
  const [data, setData] = useState<ImpactData>(initialData);
  const [searchQuery, setSearchQuery] = useState(currentLocationName);
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [activeProfile, setActiveProfile] = useState<string | null>(null);

  const mapRef = useRef<any>(null);
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const markerRef = useRef<any>(null);

  // Initialize mini-map
  useEffect(() => {
    if (!mapContainerRef.current || mapRef.current) return;

    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false,
      dragging: true,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      touchZoom: true
    }).setView([37.7749, -122.4194], 12);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    markerRef.current = L.circleMarker([37.7749, -122.4194], {
      radius: 12,
      fillColor: "#3b82f6",
      color: "#fff",
      weight: 4,
      opacity: 1,
      fillOpacity: 0.9
    }).addTo(mapRef.current);

    mapRef.current.on('click', (e: any) => {
      const { lat, lng } = e.latlng;
      applyLocationContext(`Pinned Location`, lat, lng, 0.45, "Manual Pin");
    });

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, []);

  const adjustAC = (val: number) => {
    setData(prev => ({ ...prev, acUnits: Math.max(0, prev.acUnits + val) }));
  };

  const updateMap = (lat: number, lng: number) => {
    if (mapRef.current) {
      mapRef.current.flyTo([lat, lng], 14, { duration: 1.5 });
      if (markerRef.current) {
        markerRef.current.setLatLng([lat, lng]);
      }
    }
  };

  const handleGetLocation = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (!navigator.geolocation) return;
    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        applyLocationContext(`My GPS Position`, latitude, longitude, 0.45, "Active Tracking");
        setIsLocating(false);
        setShowResults(false);
      },
      () => setIsLocating(false)
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash-latest",
        contents: `Find precise coordinates and carbon data for: "${searchQuery}". 
                   Determine regional grid carbon intensity (gCO2/kWh).
                   Format: "LOCATION: [Name] | COORDS: [LAT, LNG] | INTENSITY: [Value] | PROFILE: [Eco Level]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const co2Match = response.text.match(/INTENSITY:\s*(\d+)/i);
      const profileMatch = response.text.match(/PROFILE:\s*([^|]+)/i);
      const intensityValue = co2Match ? parseInt(co2Match[1]) / 1000 : 0.45;
      const ecoLabel = profileMatch ? profileMatch[1].trim() : "Verified Grid";

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      let lat = 0, lng = 0;
      if (coordMatch) {
        lat = parseFloat(coordMatch[1]);
        lng = parseFloat(coordMatch[2]);
      }

      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({ 
          title: c.maps.title || "Target Area", 
          uri: c.maps.uri,
          ecoIntensity: ecoLabel,
          co2Factor: intensityValue,
          lat, lng
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      if (coordMatch && !showResults) {
        applyLocationContext(searchQuery, lat, lng, intensityValue, ecoLabel);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const applyLocationContext = (name: string, lat: number, lng: number, co2Factor: number, profile: string) => {
    setSearchQuery(name);
    onLocationChange?.({
      name,
      lat,
      lng,
      aqi: 45, pm25: 10, no2: 5
    });
    if (lat !== 0 && lng !== 0) updateMap(lat, lng);
    setData(prev => ({ ...prev, co2Rate: co2Factor }));
    setActiveProfile(profile);
  };

  const selectResult = (result: SearchResult) => {
    setShowResults(false);
    applyLocationContext(result.title, result.lat || 0, result.lng || 0, result.co2Factor || 0.45, result.ecoIntensity || "Regional Profile");
  };

  return (
    <div className="min-h-screen bg-slate-50/50 pb-32">
      {/* Unified Status Bar & Search Hub */}
      <header className="sticky top-0 z-[100] bg-white/95 backdrop-blur-2xl border-b border-slate-100 px-4 py-3 shadow-sm">
        <div className="flex flex-col gap-3">
          <div className="flex items-center justify-between px-2">
            <div className="flex items-center gap-2">
              <span className="material-symbols-outlined text-primary text-sm animate-pulse">explore</span>
              <span className="text-[9px] font-black text-slate-400 uppercase tracking-[0.2em]">Carbon Tracker  Live</span>
            </div>
            {activeProfile && (
              <div className="flex items-center gap-1.5 bg-emerald-50 border border-emerald-100 px-2 py-0.5 rounded-full">
                <span className="material-symbols-outlined text-[10px] text-emerald-600">verified</span>
                <span className="text-[8px] font-black text-emerald-600 uppercase tracking-tighter">{activeProfile}</span>
              </div>
            )}
          </div>

          {/* Inline Search in Status Bar */}
          <div className="relative">
            <form onSubmit={handleSearch} className="flex items-center bg-slate-50 border border-slate-200 rounded-2xl p-1 pr-3 focus-within:ring-2 focus-within:ring-primary/20 transition-all">
              <div className="pl-3 pr-2 py-2 flex items-center">
                <span className={`material-symbols-outlined text-slate-400 text-lg ${isLocating ? 'animate-spin' : ''}`}>
                  {isLocating ? 'sync' : 'location_on'}
                </span>
              </div>
              <input 
                className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-bold text-slate-800 placeholder:text-slate-400 py-2" 
                placeholder="Search area to analyze map..." 
                type="text" 
                value={searchQuery}
                onFocus={() => searchResults.length > 0 && setShowResults(true)}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <button type="button" onClick={handleGetLocation} className="p-2 text-primary hover:bg-white rounded-xl transition-all active:scale-90">
                <span className="material-symbols-outlined text-lg">my_location</span>
              </button>
            </form>

            {/* Sticky Dropdown Results */}
            {showResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-2 bg-white/98 backdrop-blur-3xl rounded-2xl shadow-2xl border border-slate-100 overflow-hidden py-2 animate-in fade-in slide-in-from-top-2">
                {searchResults.map((result, i) => (
                  <button key={i} onClick={() => selectResult(result)} className="w-full px-4 py-3 flex items-start gap-3 hover:bg-slate-50 text-left group">
                    <div className="size-8 rounded-lg bg-slate-100 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors">
                      <span className="material-symbols-outlined text-base">push_pin</span>
                    </div>
                    <div className="flex flex-col min-w-0 flex-1 justify-center">
                      <div className="flex items-center justify-between">
                        <span className="text-[13px] font-bold text-slate-800 truncate">{result.title}</span>
                        <span className="text-[8px] font-black text-emerald-600 bg-emerald-50 px-1 rounded">{result.ecoIntensity}</span>
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Hero Map Section - Direct access from typed location */}
      <section className="relative w-full h-[280px] bg-slate-200 overflow-hidden shadow-inner group">
        <div ref={mapContainerRef} className="w-full h-full contrast-[0.95]" />

        {/* Map Overlays */}
        <div className="absolute inset-x-0 bottom-0 h-32 bg-gradient-to-t from-slate-900/50 to-transparent pointer-events-none" />

        <div className="absolute top-4 right-4 flex flex-col gap-2">
           <button 
             onClick={onNavigateToMap}
             className="size-10 rounded-2xl bg-white/90 backdrop-blur-xl flex items-center justify-center text-slate-800 shadow-xl border border-white active:scale-95 transition-all"
           >
             <span className="material-symbols-outlined">map</span>
           </button>
        </div>

        <div className="absolute bottom-6 left-6 flex flex-col gap-1">
           <div className="flex items-center gap-2">
             <div className="size-2 rounded-full bg-blue-500 animate-ping"></div>
             <span className="text-[10px] font-black text-white uppercase tracking-widest drop-shadow-lg">Map Tracking Active</span>
           </div>
           <p className="text-[11px] text-white/80 font-bold drop-shadow-md">{currentLocationName}</p>
        </div>
      </section>

      <div className="p-4 pt-8 max-w-lg mx-auto">
        {/* Dynamic Emission Inputs */}
        <section className="mb-10">
          <div className="flex items-center gap-2 mb-4 px-1">
            <span className="material-symbols-outlined text-primary text-sm">energy_savings_leaf</span>
            <h3 className="text-slate-900 text-[11px] font-black uppercase tracking-widest opacity-60">Regional Carbon Factors</h3>
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm relative overflow-hidden">
              <div className="flex flex-col gap-1">
                <p className="text-slate-400 text-[9px] font-black uppercase tracking-tighter">Grid kgCO2 / kWh</p>
                <input 
                  className="w-full border-none p-0 bg-transparent text-2xl font-black text-slate-800 focus:ring-0" 
                  placeholder="0.0" type="number" step="0.01"
                  value={data.co2Rate || ''}
                  onChange={e => setData(prev => ({ ...prev, co2Rate: parseFloat(e.target.value) || 0 }))}
                />
              </div>
              <div className="absolute bottom-0 left-0 w-full h-1 bg-primary/5">
                <div className="h-full bg-primary transition-all duration-700" style={{ width: `${Math.min(data.co2Rate * 120, 100)}%` }}></div>
              </div>
            </div>
            <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm">
              <p className="text-slate-400 text-[9px] font-black uppercase tracking-tighter mb-1">Daily Gas Units</p>
              <input 
                className="w-full border-none p-0 bg-transparent text-2xl font-black text-slate-800 focus:ring-0" 
                placeholder="0" type="number"
                value={data.gasLevels || ''}
                onChange={e => setData(prev => ({ ...prev, gasLevels: parseFloat(e.target.value) || 0 }))}
              />
            </div>
          </div>
        </section>

        {/* Energy Consumption Section */}
        <section className="mb-10 p-8 rounded-[3rem] bg-slate-900 text-white shadow-2xl shadow-slate-900/30">
          <div className="flex items-center gap-4 mb-10">
            <div className="size-12 rounded-2xl bg-white/10 flex items-center justify-center text-primary border border-white/5">
              <span className="material-symbols-outlined text-2xl">bolt</span>
            </div>
            <div>
               <h3 className="text-lg font-black leading-tight">Consumption Metrics</h3>
               <p className="text-[10px] text-white/40 font-bold uppercase tracking-[0.2em] mt-0.5">Adjust power inputs</p>
            </div>
          </div>

          <div className="space-y-12">
            <div className="flex items-center justify-between">
              <div className="flex flex-col">
                <span className="font-black text-sm uppercase tracking-wide">AC Units</span>
                <span className="text-[10px] text-white/30 font-medium">Active cooling capacity</span>
              </div>
              <div className="flex items-center gap-5 bg-white/5 rounded-2xl p-2 border border-white/5 shadow-inner">
                <button onClick={() => adjustAC(-1)} className="size-10 flex items-center justify-center rounded-xl bg-white/5 text-white active:scale-90 transition-all hover:bg-white/10">
                  <span className="material-symbols-outlined text-lg">remove</span>
                </button>
                <span className="w-4 text-center font-black text-xl">{data.acUnits}</span>
                <button onClick={() => adjustAC(1)} className="size-10 flex items-center justify-center rounded-xl bg-primary text-white shadow-lg shadow-primary/30 active:scale-90 transition-all">
                  <span className="material-symbols-outlined text-lg">add</span>
                </button>
              </div>
            </div>

            <div className="flex flex-col gap-5">
              <div className="flex justify-between items-end">
                <div className="flex flex-col">
                  <span className="font-black text-sm uppercase tracking-wide">Daily Fan Run</span>
                  <span className="text-[10px] text-white/30">Ventilation and exhaust</span>
