}

const Dashboard: React.FC<DashboardProps> = ({ location, onLocationChange, onShowEmergency, onViewImpact, onProfileClick }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isLocating, setIsLocating] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  const handleGetLocation = () => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    setIsLocating(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        // Simulating data for current location
        setTimeout(() => {
          onLocationChange({
            name: `Current Location (${latitude.toFixed(2)}, ${longitude.toFixed(2)})`,
            lat: latitude,
            lng: longitude,
            aqi: Math.floor(Math.random() * 60) + 10,
            pm25: parseFloat((Math.random() * 15 + 5).toFixed(1)),
            no2: parseFloat((Math.random() * 10 + 2).toFixed(1))
          });
          setIsLocating(false);
          setSearchQuery('');
          setShowResults(false);
        }, 1500);
      },
      (error) => {
        setIsLocating(false);
        alert('Unable to retrieve your location. ' + error.message);
      }
    );
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsLocating(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      // Using gemini-2.5-flash as it's the required model for googleMaps grounding.
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Locate precise coordinates and place info for: "${searchQuery}". 
                   Return in format: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: {
          tools: [{ googleMaps: {} }],
        },
      });

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Target Area",
          uri: c.maps.uri
        }));

      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        onLocationChange({
          name: searchQuery,
          lat,
          lng,
          aqi: Math.floor(Math.random() * 180) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
        if (extractedResults.length === 0) setShowResults(false);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsLocating(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsLocating(true);

    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        onLocationChange({
          name: result.title,
          lat: parseFloat(geoData[0].lat),
          lng: parseFloat(geoData[0].lon),
          aqi: Math.floor(Math.random() * 180) + 20,
          pm25: parseFloat((Math.random() * 40 + 5).toFixed(1)),
          no2: parseFloat((Math.random() * 30 + 5).toFixed(1))
        });
      }
    } catch (e) {
      console.error(e);
    } finally {
      setIsLocating(false);
    }
  };

  const aqiColorClass = location.aqi <= 50 ? 'text-aqi-good' : 
                        location.aqi <= 100 ? 'text-aqi-moderate' : 
                        location.aqi <= 200 ? 'text-aqi-unhealthy' : 'text-aqi-severe';

  const aqiBgClass = location.aqi <= 50 ? 'bg-aqi-good/20' : 
                     location.aqi <= 100 ? 'bg-aqi-moderate/20' : 
                     location.aqi <= 200 ? 'bg-aqi-unhealthy/20' : 'bg-aqi-severe/20';

  const aqiBorderClass = location.aqi <= 50 ? 'border-aqi-good/30' : 
                         location.aqi <= 100 ? 'border-aqi-moderate/30' : 
                         location.aqi <= 200 ? 'border-aqi-unhealthy/30' : 'border-aqi-severe/30';

  const aqiLabel = location.aqi <= 50 ? 'Healthy' : 
                   location.aqi <= 100 ? 'Moderate' : 
                   location.aqi <= 200 ? 'Unhealthy' : 'Hazardous';

  const aqiStrokeColor = location.aqi <= 50 ? '#13ec5b' : 
                         location.aqi <= 100 ? '#fde047' : 
                         location.aqi <= 200 ? '#fb923c' : '#ef4444';

  const isHazardous = location.aqi >= 200;

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-[#e0effe] to-[#f0f7ff] p-4 relative">
      {/* Dynamic Hazardous Banner */}
      {isHazardous && (
        <div 
          onClick={onShowEmergency}
          className="fixed top-0 left-0 right-0 z-[200] bg-red-600 text-white px-4 py-3 flex items-center justify-between shadow-lg animate-in slide-in-from-top duration-500 cursor-pointer"
        >
          <div className="flex items-center gap-3">
            <span className="material-symbols-outlined fill-icon animate-pulse">report</span>
            <div className="flex flex-col">
              <span className="text-[10px] font-black uppercase tracking-[0.2em] leading-none mb-1 opacity-80">Severe Alert</span>
              <span className="text-xs font-bold">Hazardous levels detected nearby!</span>
            </div>
          </div>
          <span className="material-symbols-outlined text-sm">chevron_right</span>
        </div>
      )}

      {/* Header with Google-Style Search */}
      <header className={`flex flex-col pt-6 pb-2 gap-4 relative z-[100] transition-all duration-500 ${isHazardous ? 'mt-12' : ''}`}>
        <div className="flex items-center justify-between px-1">
          <button className="p-2 -ml-2 text-pale-blue-900/70">
            <span className="material-symbols-outlined">menu</span>
          </button>
          <div className="flex flex-col items-center">
            <span className="text-[10px] uppercase tracking-widest text-primary font-bold">AirGuard</span>
            <div className="flex items-center gap-1">
              <span className={`material-symbols-outlined text-sm text-primary`}>location_on</span>
              <h2 className="text-pale-blue-900 text-sm font-semibold truncate max-w-[180px]">
                {location.name}
              </h2>
            </div>
          </div>
          <button 
            onClick={onProfileClick}
            className="p-2 -mr-2 text-primary hover:bg-primary/5 rounded-full transition-all"
          >
            <span className="material-symbols-outlined text-primary">account_circle</span>
          </button>
        </div>

        <div className="relative">
          <form onSubmit={handleSearch} className="flex items-center bg-white/90 backdrop-blur-2xl rounded-full border border-white shadow-xl p-1.5 pr-3 transition-all duration-300 focus-within:ring-4 focus-within:ring-primary/10">
            <div className="pl-4 pr-3 py-2 flex items-center pointer-events-none">
              <span className={`material-symbols-outlined text-slate-400 ${isLocating ? 'animate-spin' : ''}`}>
                {isLocating ? 'sync' : 'search'}
              </span>
            </div>
            <input 
              className="flex-1 bg-transparent border-none focus:ring-0 text-sm font-semibold text-slate-800 placeholder:text-slate-400 py-2.5" 
              placeholder="Search city, landmark, or area..." 
              type="text" 
              value={searchQuery}
              onFocus={() => searchResults.length > 0 && setShowResults(true)}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
              <button 
                type="button" 
                onClick={() => { setSearchQuery(''); setSearchResults([]); setShowResults(false); }}
                className="p-2 text-slate-400 hover:text-slate-600 transition-colors"
              >
                <span className="material-symbols-outlined text-lg">close</span>
              </button>
            )}
            <div className="w-[1px] h-6 bg-slate-200 mx-1"></div>
            <button 
              type="button" 
              onClick={handleGetLocation}
              className="p-2.5 rounded-full text-primary hover:bg-primary/5 transition-all"
            >
              <span className="material-symbols-outlined text-[20px]">my_location</span>
            </button>
          </form>

          {/* Autocomplete Results */}
          {showResults && searchResults.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-3 bg-white/98 backdrop-blur-3xl rounded-[2rem] shadow-2xl border border-white/80 overflow-hidden py-3 animate-in fade-in slide-in-from-top-2 duration-300">
              <div className="px-5 pb-2 mb-2 border-b border-slate-50 flex items-center justify-between">
                <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Suggestions</span>
                <span className="text-[9px] font-bold text-blue-500 uppercase tracking-tighter">Powered by Google</span>
              </div>
              {searchResults.map((result, i) => (
                <div key={i} className="flex flex-col">
                  <button 
                    type="button"
                    onClick={() => selectResult(result)}
                    className="w-full px-5 py-3.5 flex items-start gap-4 hover:bg-slate-50 transition-colors text-left group"
                  >
                    <div className="size-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:text-primary transition-colors shrink-0">
                      <span className="material-symbols-outlined text-[20px]">location_on</span>
                    </div>
                    <div className="flex flex-col min-w-0 flex-1">
                      <span className="text-sm font-bold text-slate-800 truncate">{result.title}</span>
                      <span className="text-[10px] text-slate-400 truncate mt-0.5">Verified Place</span>
                    </div>
                  </button>
                  {result.uri && (
                    <a 
                      href={result.uri} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="ml-19 mr-5 mb-2 text-[9px] font-black text-primary/60 uppercase tracking-widest hover:text-primary transition-colors flex items-center gap-1"
                    >
                      <span className="material-symbols-outlined text-[12px]">open_in_new</span>
                      View on Google Maps
                    </a>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </header>

      {/* Main Dashboard Content */}
      <div className={`flex-1 flex flex-col items-center py-8 transition-opacity duration-300 ${showResults ? 'opacity-20 pointer-events-none' : 'opacity-100'}`}>
        <div className="relative flex items-center justify-center">
          <div className={`w-64 h-64 rounded-full border-[12px] border-white/80 shadow-inner flex items-center justify-center relative bg-white/20 transition-all duration-700 ${isLocating ? 'opacity-30 scale-95 blur-sm' : 'opacity-100'}`}>
            <svg className="absolute inset-0 w-full h-full -rotate-90" viewBox="0 0 100 100">
              <circle 
                style={{ color: aqiStrokeColor }}
                cx="50" cy="50" r="44" 
                fill="transparent" 
                stroke="currentColor" 
                strokeDasharray="276" 
                strokeDashoffset={276 - (276 * Math.min(location.aqi, 200) / 200)} 
                strokeLinecap="round" 
                strokeWidth="8" 
                className="transition-all duration-1000 ease-out"
              />
            </svg>
            <div className="flex flex-col items-center text-center z-10">
              <span className="text-slate-500 text-[10px] font-bold tracking-tighter uppercase mb-1">Current AQI</span>
              <h1 className="text-pale-blue-900 text-7xl font-extrabold tracking-tight">{location.aqi}</h1>
              <div className={`mt-2 px-4 py-1.5 rounded-full border shadow-sm transition-colors ${aqiBgClass} ${aqiBorderClass}`}>
                <p className={`text-sm font-bold ${aqiColorClass}`}>{aqiLabel}</p>
              </div>
            </div>
          </div>
          {isLocating && (
             <div className="absolute inset-0 flex items-center justify-center">
                <span className="material-symbols-outlined text-primary text-5xl animate-spin">sync</span>
             </div>
          )}
        </div>

        {/* Mini Stats */}
        <div className={`grid grid-cols-2 gap-4 w-full mt-8 transition-all duration-500 ${isLocating ? 'opacity-50 blur-[2px]' : ''}`}>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">filter_alt</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">PM 2.5</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.pm25}</span>
              <span className="text-[10px] text-slate-400 font-medium">g/m</span>
            </div>
          </div>
          <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl border border-white shadow-sm">
            <div className="flex items-center gap-2 mb-2">
              <span className="material-symbols-outlined text-primary text-sm">co2</span>
              <span className="text-[10px] text-slate-500 font-bold uppercase tracking-wide">NO2</span>
            </div>
            <div className="flex items-baseline gap-1">
              <span className="text-xl font-bold text-slate-800">{location.no2}</span>
              <span className="text-[10px] text-slate-400 font-medium">ppb</span>
            </div>
          </div>
        </div>

        {/* Daily Impact Card */}
        <div className="w-full bg-white/40 border border-white/60 backdrop-blur-sm p-5 rounded-3xl mt-6 shadow-sm">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h3 className="text-pale-blue-900 font-bold text-lg leading-tight">Your Daily Impact</h3>
              <p className="text-primary text-[10px] font-medium mt-1">Based on local transport & air data</p>
            </div>
            <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
              <span className="material-symbols-outlined text-primary">eco</span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <div className="flex flex-col">
              <span className="text-2xl font-bold text-slate-800">2.4<span className="text-sm font-normal text-slate-400 ml-1">kg CO2</span></span>
              <span className="text-[10px] text-emerald-600 font-bold tracking-wider mt-1">
                <span className="material-symbols-outlined text-[10px] align-middle mr-1">trending_down</span>
                -12% FROM YESTERDAY
              </span>
            </div>
            <button 
              onClick={onViewImpact}
              className="bg-primary text-white shadow-md shadow-primary/20 px-4 py-2 rounded-xl text-[10px] font-bold hover:bg-primary/90 transition-all"
            >
              VIEW TIPS
            </button>
          </div>
        </div>

        {/* Emergency Button */}
        <div className="w-full mt-auto pt-8">
          <button 
            onClick={onShowEmergency}
            className={`relative w-full group overflow-hidden transition-all duration-300 ${isHazardous ? 'scale-105 shadow-xl shadow-red-500/20' : ''}`}
          >
            <div className={`absolute inset-0 ${isHazardous ? 'bg-red-600/20' : 'bg-aqi-severe/10'} rounded-2xl`}></div>
            <div className="relative bg-white hover:bg-slate-50 transition-colors py-4 rounded-2xl flex items-center justify-center gap-3 shadow-sm border border-aqi-severe/20">
              <span className={`material-symbols-outlined ${isHazardous ? 'text-red-600 animate-pulse' : 'text-aqi-severe'}`}>
                {isHazardous ? 'notification_important' : 'warning'}
              </span>
              <span className="text-slate-800 font-bold uppercase tracking-widest text-sm">
                {isHazardous ? 'Emergency active' : 'Emergency Protocols'}
              </span>
            </div>
          </button>
          <p className="text-center text-[10px] text-slate-400 mt-3 italic">
            {isHazardous ? 'Critical safety protocols engaged' : '"Safety instructions available for severe conditions"'}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useEffect, useRef, useState } from 'react';
import { LocationData } from '../types';
import { GoogleGenAI } from "@google/genai";
import { XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area, Legend } from 'recharts';

// Declare L for Leaflet global from script tag
declare const L: any;

interface MapScreenProps {
  currentLocation: LocationData;
  onLocationSelect: (location: LocationData) => void;
}

interface SearchResult {
  title: string;
  uri: string;
  snippet?: string;
  lat?: number;
  lng?: number;
}

interface WeatherData {
  temp: number;
  feelsLike: number;
  humidity: number;
  wind: number;
  precipitation: number;
  condition: string;
  conditionText: string;
}

interface HistoryPoint {
  date: string;
  fullDate: string;
  aqi: number;
  carbon: number;
}

type HistoryViewMode = 'aqi' | 'carbon' | 'both';
type HeatmapMode = 'off' | 'live' | 'historical';

const MapScreen: React.FC<MapScreenProps> = ({ currentLocation, onLocationSelect }) => {
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapRef = useRef<any>(null);
  const selectionMarkerRef = useRef<any>(null);
  const selectionCircleRef = useRef<any>(null);
  const heatmapLayerRef = useRef<any>([]);

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCoords, setSelectedCoords] = useState<{ lat: number, lng: number }>({
    lat: currentLocation.lat || 37.7749,
    lng: currentLocation.lng || -122.4194
  });
  const [previewLocation, setPreviewLocation] = useState<LocationData>({
    ...currentLocation,
    carbonIntensity: currentLocation.carbonIntensity || 450
  });
  const [isSearching, setIsSearching] = useState(false);
  const [isGeocoding, setIsGeocoding] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);

  // Heatmap State
  const [heatmapMode, setHeatmapMode] = useState<HeatmapMode>('live');
  const [heatmapDate, setHeatmapDate] = useState<string>(new Date().toISOString().split('T')[0]);

  // Weather state
  const [showWeather, setShowWeather] = useState(false);
  const [weatherData, setWeatherData] = useState<WeatherData | null>(null);
  const [isFetchingWeather, setIsFetchingWeather] = useState(false);

  // History state
  const [showHistory, setShowHistory] = useState(false);
  const [historyViewMode, setHistoryViewMode] = useState<HistoryViewMode>('both');
  const [historicalData, setHistoricalData] = useState<HistoryPoint[]>([]);
  const [historyBaseDate, setHistoryBaseDate] = useState<string>(new Date().toISOString().split('T')[0]);

  // Helper to get theme based on AQI
  const getAqiTheme = (aqi: number) => {
    if (aqi <= 50) return { color: '#10b981', label: 'Healthy', bg: 'bg-emerald-500', text: 'text-emerald-500' };
    if (aqi <= 100) return { color: '#f59e0b', label: 'Moderate', bg: 'bg-amber-500', text: 'text-amber-500' };
    if (aqi <= 200) return { color: '#f97316', label: 'Unhealthy', bg: 'bg-orange-500', text: 'text-orange-500' };
    return { color: '#e11d48', label: 'Hazardous', bg: 'bg-rose-600', text: 'text-rose-600' };
  };

  const generateHistory = (currentAqi: number, currentCarbon: number, baseDateStr: string) => {
    const data: HistoryPoint[] = [];
    const baseDate = new Date(baseDateStr);
    for (let i = 6; i >= 0; i--) {
      const d = new Date(baseDate);
      d.setDate(baseDate.getDate() - i);
      const isToday = i === 0;
      data.push({
        date: d.toLocaleDateString('en-US', { weekday: 'short' }),
        fullDate: d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
        aqi: isToday ? currentAqi : Math.max(10, Math.floor(currentAqi + (Math.random() - 0.5) * 80)),
        carbon: isToday ? currentCarbon : Math.max(50, Math.floor(currentCarbon + (Math.random() - 0.5) * 150))
      });
    }
    setHistoricalData(data);
  };

  const fetchWeather = async (lat: number, lng: number) => {
    setIsFetchingWeather(true);
    try {
      const response = await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lng}&current=temperature_2m,relative_humidity_2m,apparent_temperature,precipitation,weather_code,wind_speed_10m&wind_speed_unit=ms`);
      const data = await response.json();

      if (data.current) {
        setWeatherData({
          temp: data.current.temperature_2m,
          feelsLike: data.current.apparent_temperature,
          humidity: data.current.relative_humidity_2m, 
          wind: data.current.wind_speed_10m,
          precipitation: data.current.precipitation,
          condition: getWeatherIcon(data.current.weather_code),
          conditionText: getWeatherConditionText(data.current.weather_code)
        });
      }
    } catch (err) {
      console.error("Failed to fetch weather:", err);
    } finally {
      setIsFetchingWeather(false);
    }
  };

  const getWeatherIcon = (code: number) => {
    if (code === 0) return 'sunny';
    if (code <= 3) return 'cloud';
    if (code <= 48) return 'foggy';
    if (code <= 67) return 'rainy';
    if (code <= 77) return 'ac_unit';
    if (code <= 82) return 'rainy';
    if (code <= 99) return 'thunderstorm';
    return 'wb_cloudy';
  };

  const getWeatherConditionText = (code: number) => {
    if (code === 0) return 'Clear Sky';
    if (code <= 3) return 'Partly Cloudy';
    if (code <= 48) return 'Foggy';
    if (code <= 67) return 'Light Rain';
    if (code <= 77) return 'Snowing';
    if (code <= 82) return 'Heavy Rain';
    if (code <= 99) return 'Stormy';
    return 'Cloudy';
  };

  useEffect(() => {
    if (!mapContainerRef.current) return;

    if (mapRef.current) {
        mapRef.current.remove();
    }

    mapRef.current = L.map(mapContainerRef.current, {
      zoomControl: false,
      attributionControl: false
    }).setView([selectedCoords.lat, selectedCoords.lng], 12);

    L.tileLayer('https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png', {
      maxZoom: 19
    }).addTo(mapRef.current);

    const theme = getAqiTheme(previewLocation.aqi);

    selectionMarkerRef.current = L.marker([selectedCoords.lat, selectedCoords.lng], {
      draggable: true,
      icon: L.divIcon({
        className: 'selection-marker',
        html: createMarkerHtml(theme.color, previewLocation.aqi, theme.label),
        iconSize: [100, 60],
        iconAnchor: [50, 60]
      })
    }).addTo(mapRef.current);

    selectionCircleRef.current = L.circle([selectedCoords.lat, selectedCoords.lng], {
      color: theme.color,
      fillColor: theme.color,
      fillOpacity: 0.1,
      radius: 1200,
      weight: 1
    }).addTo(mapRef.current);

    mapRef.current.on('click', (e: any) => {
      updateLocation(e.latlng.lat, e.latlng.lng);
      setShowResults(false);
    });

    if (selectionMarkerRef.current) {
        selectionMarkerRef.current.on('dragend', (e: any) => {
            const { lat, lng } = e.target.getLatLng();
            updateLocation(lat, lng);
        });
    }

    generateHeatmap(heatmapDate);
    fetchWeather(selectedCoords.lat, selectedCoords.lng);
    generateHistory(previewLocation.aqi, previewLocation.carbonIntensity || 450, historyBaseDate);

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
      selectionMarkerRef.current = null;
      selectionCircleRef.current = null;
    };
  }, []);

  useEffect(() => {
    if (!mapRef.current) return;

    // Clear old heatmap
    if (heatmapLayerRef.current) {
        heatmapLayerRef.current.forEach((layer: any) => layer && layer.remove());
        heatmapLayerRef.current = [];
    }

    if (heatmapMode !== 'off') {
      generateHeatmap(heatmapMode === 'historical' ? heatmapDate : 'live');
    }
  }, [heatmapMode, heatmapDate, selectedCoords]);

  const generateHeatmap = (dateOrLive: string) => {
    if (!mapRef.current) return;

    const center = selectedCoords;
    const points = [];

    // Deterministic random generator for historical consistency
    const pseudoRandom = (seed: string) => {
      let h = 0;
      for (let i = 0; i < seed.length; i++) h = Math.imul(31, h) + seed.charCodeAt(i) | 0;
      return function() {
        h = Math.imul(h ^ h >>> 16, 0x85ebca6b);
        h = Math.imul(h ^ h >>> 13, 0xc2b2ae35);
        return ((h ^= h >>> 16) >>> 0) / 4294967296;
      };
    };

    const rnd = pseudoRandom(dateOrLive + center.lat + center.lng);

    for(let i=0; i<12; i++) {
       const offsetLat = (rnd() - 0.5) * 0.15;
       const offsetLng = (rnd() - 0.5) * 0.15;
       const randomAqi = Math.floor(rnd() * 250);
       const theme = getAqiTheme(randomAqi);

       const circle = L.circle([center.lat + offsetLat, center.lng + offsetLng], {
         color: 'transparent',
         fillColor: theme.color,
         fillOpacity: 0.22,
         radius: 4000
       });
       if (mapRef.current) circle.addTo(mapRef.current);
       points.push(circle);
    }
    heatmapLayerRef.current = points;
  };

  const createMarkerHtml = (color: string, aqi: number, label: string) => `
    <div class="marker-info-bubble" style="--marker-color: ${color}">
      <div class="bubble-content">
        <span class="bubble-label">${label}</span>
        <span class="bubble-aqi">${aqi}</span>
      </div>
      <div class="bubble-tail"></div>
    </div>
  `;

  const updateLocation = (lat: number, lng: number, name?: string, shouldPan = false) => {
    if (!mapRef.current) return;

    setSelectedCoords({ lat, lng });
    if (selectionMarkerRef.current) selectionMarkerRef.current.setLatLng([lat, lng]);
    if (selectionCircleRef.current) selectionCircleRef.current.setLatLng([lat, lng]);
    if (shouldPan && mapRef.current) mapRef.current.flyTo([lat, lng], 13);

    setIsSearching(true);
    fetchWeather(lat, lng);

    setTimeout(() => {
      const mockAqi = Math.floor(Math.random() * 250);
      const theme = getAqiTheme(mockAqi);
      const mockCarbon = Math.floor(Math.random() * 600) + 100;

      const newLoc: LocationData = {
        name: name || `Coords: ${lat.toFixed(4)}, ${lng.toFixed(4)}`,
        lat: lat,
        lng: lng,
        aqi: mockAqi,
        pm25: parseFloat((Math.random() * 30 + 2).toFixed(1)),
        no2: parseFloat((Math.random() * 15 + 1).toFixed(1)),
        carbonIntensity: mockCarbon
      };

      setPreviewLocation(newLoc);
      setIsSearching(false);
      generateHistory(mockAqi, mockCarbon, historyBaseDate);

      if (selectionCircleRef.current) selectionCircleRef.current.setStyle({ color: theme.color, fillColor: theme.color });
      if (selectionMarkerRef.current) {
        selectionMarkerRef.current.setIcon(L.divIcon({
          className: 'selection-marker',
          html: createMarkerHtml(theme.color, mockAqi, theme.label),
          iconSize: [100, 60],
          iconAnchor: [50, 60]
        }));

        const popupContent = `
          <div class="p-3 min-w-[160px] bg-white rounded-xl shadow-xl">
            <div class="flex items-center gap-2 mb-2">
              <span class="material-symbols-outlined text-sm text-primary">analytics</span>
              <span class="text-[9px] font-black uppercase text-slate-400">Regional Analytics</span>
            </div>
            <div class="flex flex-col gap-2">
              <div class="flex justify-between items-center bg-slate-50 p-2 rounded-lg">
                <span class="text-[10px] font-bold text-slate-500 uppercase">Live AQI</span>
                <span class="text-sm font-black ${theme.text}">${mockAqi}</span>
              </div>
              <div class="flex justify-between items-center bg-slate-50 p-2 rounded-lg">
                <span class="text-[10px] font-bold text-slate-500 uppercase">Grid CO</span>
                <span class="text-sm font-black text-slate-800">${mockCarbon}g</span>
              </div>
            </div>
          </div>
        `;
        selectionMarkerRef.current.bindPopup(popupContent, {
          closeButton: false,
          offset: [0, -40],
          className: 'custom-leaflet-popup'
        }).openPopup();
      }
    }, 800);
  };

  const handleSearch = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsGeocoding(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: `Locate precise coordinates and place details for: "${searchQuery}". Format: "LOCATION: [Name] | COORDS: [LAT, LNG]"`,
        config: { tools: [{ googleMaps: {} }] },
      });

      const coordMatch = response.text.match(/COORDS:\s*\[\s*(-?\d+\.?\d*)\s*,\s*(-?\d+\.?\d*)\s*\]/i);

      const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
      const extractedResults: SearchResult[] = chunks
        .filter(c => c.maps)
        .map(c => ({
          title: c.maps.title || "Found Area",
          uri: c.maps.uri,
          snippet: ""
        }));
      setSearchResults(extractedResults);
      setShowResults(extractedResults.length > 0);

      if (coordMatch && extractedResults.length === 0) {
        const lat = parseFloat(coordMatch[1]);
        const lng = parseFloat(coordMatch[2]);
        updateLocation(lat, lng, searchQuery, true);
        setShowResults(false);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setIsGeocoding(false);
    }
  };

  const selectResult = async (result: SearchResult) => {
    setShowResults(false);
    setSearchQuery(result.title);
    setIsGeocoding(true);
    try {
      const geoResponse = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(result.title)}`);
      const geoData = await geoResponse.json();
      if (geoData?.[0]) {
        updateLocation(parseFloat(geoData[0].lat), parseFloat(geoData[0].lon), result.title, true);
      }
    } catch (e) { console.error(e); } finally { setIsGeocoding(false); }
  };

  const handleBaseDateChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newDate = e.target.value;
    setHistoryBaseDate(newDate);
    generateHistory(previewLocation.aqi, previewLocation.carbonIntensity || 450, newDate);
  };

  const toggleHeatmap = () => {
    if (heatmapMode === 'off') setHeatmapMode('live');
    else if (heatmapMode === 'live') setHeatmapMode('historical');
    else setHeatmapMode('off');
